// AdminPage.js
import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { collection, getDocs, doc, getDoc, setDoc } from "firebase/firestore";
import "./AdminPage.css";

function AdminPage({ onLogout, goToPointSystem }) {
  const [mealCounts, setMealCounts] = useState({});
  const [userPoints, setUserPoints] = useState({});
  const [loading, setLoading] = useState(true);

  // default rules (used only if settings/points not present)
  const defaultSystem = {
    X: 1,
    "0": 0,
    B: 0.25,
    L: 0.35,
    S: 0.45,
    "B/L": 0.65,
    "B/S": 0.75,
    "L/S": 0.85,
  };

  useEffect(() => {
    const fetchAll = async () => {
      try {
        // load point system
        const settingsRef = doc(db, "settings", "points");
        const settingsSnap = await getDoc(settingsRef);
        const pointSystem = settingsSnap.exists()
          ? settingsSnap.data()
          : defaultSystem;
        if (!settingsSnap.exists()) await setDoc(settingsRef, defaultSystem);

        // load users
        const usersRef = collection(db, "users");
        const querySnapshot = await getDocs(usersRef);

        const counts = {};
        const points = {};

        const today = new Date();
        const todayStr = today.toISOString().split("T")[0];
        const currentMonth = today.getMonth();
        const currentYear = today.getFullYear();

        // ‚úÖ next 8 days (today + 7 more)
        const next8Days = [];
        for (let i = 0; i < 8; i++) {
          const d = new Date(today);
          d.setDate(today.getDate() + i);
          const dateStr = d.toISOString().split("T")[0];
          next8Days.push(dateStr);
          counts[dateStr] = { breakfast: 0, lunch: 0, supper: 0 };
        }

        const totalUsers = querySnapshot.size; // total number of users

        querySnapshot.forEach((docSnap) => {
          const data = docSnap.data();
          const email = data.email || "Unknown";
          const userMeals = data.meals || {};

          let totalPoints = 0;

          // calculate monthly points up to today using pointSystem
          Object.entries(userMeals).forEach(([day, meals]) => {
            const dateObj = new Date(day);
            const dateStr = day;
            if (
              dateObj.getMonth() === currentMonth &&
              dateObj.getFullYear() === currentYear &&
              dateStr <= todayStr
            ) {
              const marked = {
                B: !!meals.breakfast,
                L: !!meals.lunch,
                S: !!meals.supper,
              };
              const unmarked = Object.keys(marked).filter((m) => !marked[m]);
              let key = "0";
              if (unmarked.length === 3) key = "X";
              else if (unmarked.length === 0) key = "0";
              else key = unmarked.join("/");
              totalPoints += pointSystem[key] || 0;
            }
          });

          points[email] = totalPoints.toFixed(2);

          // ‚úÖ meal counts (next 8 days)
          next8Days.forEach((day) => {
            const meals = userMeals[day];

            ["breakfast", "lunch", "supper"].forEach((meal) => {
              // Default: if no record & day > today ‚Üí count as marked
              if (!meals && day > todayStr) {
                counts[day][meal] += 1;
              } else if (meals && meals[meal]) {
                counts[day][meal] += 1;
              }
            });
          });
        });

        setMealCounts(counts);
        setUserPoints(points);
      } catch (err) {
        console.error("Admin data error:", err);
      }
      setLoading(false);
    };

    fetchAll();
  }, []);

  if (loading) return <h2 className="loading">Loading data...</h2>;

  return (
    <div className="admin-container">
      <h2 className="title">üìä Admin Dashboard</h2>

      <h3>ü•ò Meal Counts (Next 8 Days)</h3>
      <table className="meal-table">
        <thead>
          <tr>
            <th>Date</th>
            <th>üç≥ Breakfast</th>
            <th>ü•ó Lunch</th>
            <th>üçõ Supper</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(mealCounts).map(([day, meals]) => (
            <tr key={day}>
              <td>{day}</td>
              <td>{meals.breakfast}</td>
              <td>{meals.lunch}</td>
              <td>{meals.supper}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>üèÜ Monthly Points</h3>
      <table className="meal-table">
        <thead>
          <tr>
            <th>Student</th>
            <th>Total Points</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(userPoints).map(([email, points]) => (
            <tr key={email}>
              <td>{email}</td>
              <td>{points}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="admin-actions">
        <button className="btn points-btn" onClick={goToPointSystem}>
          ‚öôÔ∏è Point System
        </button>
        <button className="logout-btn" onClick={onLogout}>
          Logout
        </button>
      </div>
    </div>
  );
}

export default AdminPage;
