// AdminPage.js
import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import {
  collection,
  getDocs,
  doc,
  getDoc,
  setDoc,
} from "firebase/firestore";
import "./AdminPage.css";

function AdminPage({ onLogout, goToPointSystem }) {
  const [mealCounts, setMealCounts] = useState({});
  const [rangePoints, setRangePoints] = useState({});
  const [loading, setLoading] = useState(true);

  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");

  // default rules
  const defaultSystem = {
    X: 1,
    "0": 0,
    B: 0.25,
    L: 0.35,
    S: 0.45,
    "B/L": 0.65,
    "B/S": 0.75,
    "L/S": 0.85,
  };

  const [pointSystem, setPointSystem] = useState(defaultSystem);

  useEffect(() => {
    const fetchInitial = async () => {
      try {
        // load point system
        const settingsRef = doc(db, "settings", "points");
        const settingsSnap = await getDoc(settingsRef);
        if (settingsSnap.exists()) {
          setPointSystem(settingsSnap.data());
        } else {
          await setDoc(settingsRef, defaultSystem);
        }

        // load users
        const usersRef = collection(db, "users");
        const querySnapshot = await getDocs(usersRef);

        const counts = {};
        const today = new Date();
        const todayStr = today.toISOString().split("T")[0];

        // ‚úÖ next 8 days
        const next8Days = [];
        for (let i = 0; i < 8; i++) {
          const d = new Date(today);
          d.setDate(today.getDate() + i);
          const dateStr = d.toISOString().split("T")[0];
          next8Days.push(dateStr);
          counts[dateStr] = { breakfast: 0, lunch: 0, supper: 0 };
        }

        querySnapshot.forEach((docSnap) => {
          const userMeals = docSnap.data().meals || {};

          next8Days.forEach((day) => {
            const meals = userMeals[day];
            ["breakfast", "lunch", "supper"].forEach((meal) => {
              if (!meals && day > todayStr) {
                counts[day][meal] += 1; // default marked
              } else if (meals && meals[meal]) {
                counts[day][meal] += 1;
              }
            });
          });
        });

        setMealCounts(counts);
      } catch (err) {
        console.error("Admin load error:", err);
      }
      setLoading(false);
    };

    fetchInitial();
  }, []);

  // üîπ Calculate range points
  const calculateRangePoints = async () => {
    if (!fromDate || !toDate) {
      alert("Please select both dates!");
      return;
    }

    try {
      const usersRef = collection(db, "users");
      const querySnapshot = await getDocs(usersRef);
      const results = {};

      querySnapshot.forEach((docSnap) => {
        const data = docSnap.data();
        const email = data.email || "Unknown";
        const userMeals = data.meals || {};

        let totalPoints = 0;

        Object.entries(userMeals).forEach(([day, meals]) => {
          if (day >= fromDate && day <= toDate) {
            const marked = {
              B: !!meals.breakfast,
              L: !!meals.lunch,
              S: !!meals.supper,
            };
            const unmarked = Object.keys(marked).filter((m) => !marked[m]);
            let key = "0";
            if (unmarked.length === 3) key = "X";
            else if (unmarked.length === 0) key = "0";
            else key = unmarked.join("/");

            totalPoints += pointSystem[key] || 0;
          }
        });

        results[email] = totalPoints.toFixed(2);
      });

      setRangePoints(results);
    } catch (err) {
      console.error("Error calculating range:", err);
    }
  };

  // üîπ Export to CSV
  const exportCSV = () => {
    if (!Object.keys(rangePoints).length) {
      alert("No data to export!");
      return;
    }

    let csv = "Student,Points\n";
    Object.entries(rangePoints).forEach(([email, points]) => {
      csv += `${email},${points}\n`;
    });

    const blob = new Blob([csv], { type: "text/csv;charset=utf-8;" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = `points_${fromDate}_to_${toDate}.csv`;
    link.click();
  };

  if (loading) return <h2 className="loading">Loading data...</h2>;

  return (
    <div className="admin-container">
      <h2 className="title">üìä Admin Dashboard</h2>

      <h3>ü•ò Meal Counts (Next 8 Days)</h3>
      <table className="meal-table">
        <thead>
          <tr>
            <th>Date</th>
            <th>üç≥ Breakfast</th>
            <th>ü•ó Lunch</th>
            <th>üçõ Supper</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(mealCounts).map(([day, meals]) => (
            <tr key={day}>
              <td>{day}</td>
              <td>{meals.breakfast}</td>
              <td>{meals.lunch}</td>
              <td>{meals.supper}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>üèÜ Points by Date Range</h3>
      <div className="range-box">
        <label>
          From:{" "}
          <input
            type="date"
            value={fromDate}
            onChange={(e) => setFromDate(e.target.value)}
          />
        </label>
        <label>
          To:{" "}
          <input
            type="date"
            value={toDate}
            onChange={(e) => setToDate(e.target.value)}
          />
        </label>
        <button className="btn" onClick={calculateRangePoints}>
          Calculate
        </button>
        <button className="btn export-btn" onClick={exportCSV}>
          ‚¨áÔ∏è Export CSV
        </button>
      </div>

      {Object.keys(rangePoints).length > 0 && (
        <table className="meal-table">
          <thead>
            <tr>
              <th>Student</th>
              <th>Points</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(rangePoints).map(([email, points]) => (
              <tr key={email}>
                <td>{email}</td>
                <td>{points}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <div className="admin-actions">
        <button className="btn points-btn" onClick={goToPointSystem}>
          ‚öôÔ∏è Point System
        </button>
        <button className="logout-btn" onClick={onLogout}>
          Logout
        </button>
      </div>
    </div>
  );
}

export default AdminPage;
