import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { collection, getDocs } from "firebase/firestore";

const AdminPage = () => {
  const [counts, setCounts] = useState({ breakfast: 0, lunch: 0, supper: 0 });

  useEffect(() => {
    const fetchMealCounts = async () => {
      try {
        const usersSnapshot = await getDocs(collection(db, "users"));
        let breakfast = 0, lunch = 0, supper = 0;

        usersSnapshot.forEach((doc) => {
          const meals = doc.data().meals || {};
          if (meals.breakfast) breakfast++;
          if (meals.lunch) lunch++;
          if (meals.supper) supper++;
        });

        setCounts({ breakfast, lunch, supper });
      } catch (error) {
        console.error("Error fetching meal counts:", error);
      }
    };

    fetchMealCounts();
  }, []);

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>Admin Dashboard</h2>
      <p>Breakfast Count: {counts.breakfast}</p>
      <p>Lunch Count: {counts.lunch}</p>
      <p>Supper Count: {counts.supper}</p>
    </div>
  );
};

export default AdminPage;
