import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { doc, onSnapshot } from "firebase/firestore";

function AdminPage() {
  const [counts, setCounts] = useState({ breakfast: 0, lunch: 0, supper: 0 });

  useEffect(() => {
    const meals = ["breakfast", "lunch", "supper"];
    const unsubscribers = meals.map((meal) =>
      onSnapshot(doc(db, "meals", meal), (snapshot) => {
        if (snapshot.exists()) {
          setCounts((prev) => ({
            ...prev,
            [meal]: snapshot.data().count || 0,
          }));
        }
      })
    );

    return () => unsubscribers.forEach((unsub) => unsub());
  }, []);

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>Admin Dashboard</h2>
      <p>Breakfast Count: {counts.breakfast}</p>
      <p>Lunch Count: {counts.lunch}</p>
      <p>Supper Count: {counts.supper}</p>
    </div>
  );
}

export default AdminPage;
