// AdminPage.js
import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import {
  collection,
  getDocs,
  doc,
  getDoc,
  setDoc,
  updateDoc,
  deleteDoc,
} from "firebase/firestore";
import "./AdminPage.css";

function AdminPage({ onLogout, goToPointSystem }) {
  const [mealCounts, setMealCounts] = useState({});
  const [userPoints, setUserPoints] = useState({});
  const [loading, setLoading] = useState(true);

  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [generated, setGenerated] = useState(false);

  const [showMeals, setShowMeals] = useState(false);
  const [showReport, setShowReport] = useState(false);
  const [showApprovals, setShowApprovals] = useState(false);

  const [todayCounts, setTodayCounts] = useState({
    breakfast: 0,
    lunch: 0,
    supper: 0,
  });

  // default rules (admin default)
  const defaultSystem = {
    X: 1,
    "0": 0,
    B: 0.25,
    L: 0.35,
    S: 0.45,
    "B/L": 0.65,
    "B/S": 0.75,
    "L/S": 0.85,
  };

  const [pointSystem, setPointSystem] = useState(defaultSystem);
  const [allUsers, setAllUsers] = useState([]);
  const [pendingUsers, setPendingUsers] = useState([]);

  // --- load settings + users + meal counts (next 8 days) --------------------
  useEffect(() => {
    const fetchAll = async () => {
      try {
        // load point system
        const settingsRef = doc(db, "settings", "points");
        const settingsSnap = await getDoc(settingsRef);
        if (settingsSnap.exists()) {
          setPointSystem(settingsSnap.data());
        } else {
          await setDoc(settingsRef, defaultSystem);
        }

        // load users
        const usersRef = collection(db, "users");
        const querySnapshot = await getDocs(usersRef);

        const all = [];
        const pending = [];
        const counts = {};
        const today = new Date();
        const todayStr = today.toISOString().split("T")[0];

        // init next 8 days
        const nextDays = [];
        for (let i = 0; i < 8; i++) {
          const d = new Date(today);
          d.setDate(today.getDate() + i);
          const dateStr = d.toISOString().split("T")[0];
          nextDays.push(dateStr);
          counts[dateStr] = { breakfast: 0, lunch: 0, supper: 0 };
        }

        querySnapshot.forEach((docSnap) => {
          const data = docSnap.data();

          if (data.role === "pending") {
            pending.push({ id: docSnap.id, ...data });
          }

          all.push(data);

          const userMeals = data.meals || {};
          nextDays.forEach((day) => {
            const meals = userMeals[day];
            if (meals) {
              ["breakfast", "lunch", "supper"].forEach((meal) => {
                if (meals[meal]) counts[day][meal] += 1;
              });
            } else if (day > todayStr) {
              counts[day].breakfast += 1;
              counts[day].lunch += 1;
              counts[day].supper += 1;
            }
          });
        });

        setAllUsers(all);
        setPendingUsers(pending);
        setMealCounts(counts);

        // today's counts only
        setTodayCounts(counts[todayStr]);
      } catch (err) {
        console.error("Admin data error:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchAll();
  }, []);

  // --- Approve / Reject User ------------------------------------------------
  const approveUser = async (userId) => {
    await updateDoc(doc(db, "users", userId), { role: "student" });
    setPendingUsers((prev) => prev.filter((u) => u.id !== userId));
  };

  const rejectUser = async (userId) => {
    await deleteDoc(doc(db, "users", userId));
    setPendingUsers((prev) => prev.filter((u) => u.id !== userId));
  };

  // --- generate report for selected date range -------------------------------
  const generateReport = () => {
    if (!fromDate || !toDate) {
      alert("⚠️ Please select both From and To dates!");
      return;
    }

    const points = {};
    const todayStr = new Date().toISOString().split("T")[0];

    allUsers.forEach((user) => {
      if (user.role === "pending") return; // skip pending users
      const email = user.email || "Unknown";
      const userMeals = user.meals || {};
      let totalPoints = 0;

      // find earliest record (if exists)
      const mealDates = Object.keys(userMeals).sort();
      const firstMealDate = mealDates.length > 0 ? mealDates[0] : todayStr;

      const start = new Date(fromDate);
      const end = new Date(toDate);
      start.setHours(0, 0, 0, 0);
      end.setHours(0, 0, 0, 0);

      for (
        let d = new Date(start.getTime());
        d.getTime() <= end.getTime();
        d.setDate(d.getDate() + 1)
      ) {
        const day = d.toISOString().split("T")[0];

        let meals = userMeals[day];
        if (!meals) {
          if (day > todayStr) {
            meals = { breakfast: true, lunch: true, supper: true };
          } else if (day >= firstMealDate) {
            meals = { breakfast: true, lunch: true, supper: true };
          } else {
            continue;
          }
        }

        const marked = {
          B: !!meals.breakfast,
          L: !!meals.lunch,
          S: !!meals.supper,
        };
        const markedArr = ["B", "L", "S"].filter((m) => marked[m]);

        let key;
        if (markedArr.length === 3) key = "X";
        else if (markedArr.length === 0) key = "0";
        else key = markedArr.join("/");

        totalPoints += pointSystem[key] ?? 0;
      }

      points[email] = totalPoints.toFixed(2);
    });

    setUserPoints(points);
    setGenerated(true);
  };

  // --- export CSV -----------------------------------------------------------
  const exportCSV = () => {
    const rows = [["Email", "Points"]];
    Object.entries(userPoints).forEach(([email, pts]) => {
      rows.push([email, pts]);
    });

    const csvContent = rows.map((r) => r.join(",")).join("\n");
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });

    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.setAttribute("download", `points_${fromDate}_to_${toDate}.csv`);
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  if (loading) return <h2 className="loading">Loading data...</h2>;

  return (
    <div className="admin-container">
      <h2 className="title">📊 Admin Dashboard</h2>

      {/* Today's Meal Summary */}
      <div className="today-summary">
        <h3>📌 Today’s Meal Count</h3>
        <p>🍳 Breakfast: <strong>{todayCounts.breakfast}</strong></p>
        <p>🥗 Lunch: <strong>{todayCounts.lunch}</strong></p>
        <p>🍛 Supper: <strong>{todayCounts.supper}</strong></p>
      </div>

      {/* Meal Counts Section */}
      <button
        className="btn toggle-btn"
        onClick={() => setShowMeals(!showMeals)}
      >
        🥘 Meal Counts (Next 8 Days)
      </button>
      {showMeals && (
        <table className="meal-table">
          <thead>
            <tr>
              <th className="date-col">Date</th>
              <th>🍳 Breakfast</th>
              <th>🥗 Lunch</th>
              <th>🍛 Supper</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(mealCounts).map(([day, meals]) => (
              <tr key={day}>
                <td className="date-col">{day}</td>
                <td>{meals.breakfast}</td>
                <td>{meals.lunch}</td>
                <td>{meals.supper}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Report Section */}
      <button
        className="btn toggle-btn"
        onClick={() => setShowReport(!showReport)}
      >
        📑 Generate Report
      </button>
      {showReport && (
        <>
          <div className="report-filters">
            <label>
              From:{" "}
              <input
                type="date"
                value={fromDate}
                onChange={(e) => setFromDate(e.target.value)}
              />
            </label>
            <label>
              To:{" "}
              <input
                type="date"
                value={toDate}
                onChange={(e) => setToDate(e.target.value)}
              />
            </label>
            <button className="btn generate-btn" onClick={generateReport}>
              Generate
            </button>
          </div>

          {generated && (
            <>
              <table className="meal-table">
                <thead>
                  <tr>
                    <th>Email</th>
                    <th>Total Points</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(userPoints).map(([email, points]) => (
                    <tr key={email}>
                      <td>{email}</td>
                      <td>{points}</td>
                    </tr>
                  ))}
                </tbody>
              </table>

              <button className="btn export-btn" onClick={exportCSV}>
                ⬇️ Download CSV
              </button>
            </>
          )}
        </>
      )}

      {/* Approval Section */}
      <button
        className="btn toggle-btn"
        onClick={() => setShowApprovals(!showApprovals)}
      >
        ✅ User Approvals
      </button>
      {showApprovals && (
        <div className="approval-list">
          {pendingUsers.length === 0 ? (
            <p>No pending requests 🎉</p>
          ) : (
            <table className="meal-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Year</th>
                  <th>Branch</th>
                  <th>Hostel</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {pendingUsers.map((u) => (
                  <tr key={u.id}>
                    <td>{u.name}</td>
                    <td>{u.email}</td>
                    <td>{u.year}</td>
                    <td>{u.branch}</td>
                    <td>{u.mea}</td>
                    <td>
                      <button
                        className="btn approve-btn"
                        onClick={() => approveUser(u.id)}
                      >
                        Approve
                      </button>
                      <button
                        className="btn reject-btn"
                        onClick={() => rejectUser(u.id)}
                      >
                        Reject
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}

      <div className="admin-actions">
        <button className="btn points-btn" onClick={goToPointSystem}>
          ⚙️ Point System
        </button>
        <button className="logout-btn" onClick={onLogout}>
          Logout
        </button>
      </div>
    </div>
  );
}

export default AdminPage;
