// AdminPage.js
import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import {
  collection,
  getDocs,
  doc,
  getDoc,
  setDoc,
  updateDoc,
  query,
  where,
} from "firebase/firestore";
import "./AdminPage.css";

function AdminPage({ onLogout, goToPointSystem }) {
  const [mealCounts, setMealCounts] = useState({});
  const [userPoints, setUserPoints] = useState({});
  const [loading, setLoading] = useState(true);

  const [month, setMonth] = useState("September");
  const [fromDate, setFromDate] = useState(1);
  const [toDate, setToDate] = useState(30);
  const [year, setYear] = useState(new Date().getFullYear());
  const [generated, setGenerated] = useState(false);

  const [allUsers, setAllUsers] = useState([]);
  const [view, setView] = useState("dashboard");

  const defaultSystem = {
    X: 1,
    "0": 0,
    B: 0.25,
    L: 0.35,
    S: 0.45,
    "B/L": 0.65,
    "B/S": 0.75,
    "L/S": 0.85,
  };
  const [pointSystem, setPointSystem] = useState(defaultSystem);

  // fetch settings + users + meal counts
  useEffect(() => {
    const fetchAll = async () => {
      try {
        const settingsRef = doc(db, "settings", "points");
        const settingsSnap = await getDoc(settingsRef);
        if (settingsSnap.exists()) {
          setPointSystem(settingsSnap.data());
        } else {
          await setDoc(settingsRef, defaultSystem);
        }

        const usersRef = collection(db, "users");
        const querySnapshot = await getDocs(usersRef);

        const all = [];
        const counts = {};
        const today = new Date();

        const nextDays = [];
        for (let i = 0; i < 8; i++) {
          const d = new Date(today);
          d.setDate(today.getDate() + i);
          const dateStr = d.toISOString().split("T")[0];
          nextDays.push(dateStr);
          counts[dateStr] = { breakfast: 0, lunch: 0, supper: 0 };
        }

        querySnapshot.forEach((docSnap) => {
          const data = docSnap.data();
          all.push(data);

          const userMeals = data.meals || {};
          nextDays.forEach((day) => {
            const meals = userMeals[day];
            if (meals) {
              ["breakfast", "lunch", "supper"].forEach((meal) => {
                if (meals[meal]) counts[day][meal] += 1;
              });
            } else {
              const todayStr = new Date().toISOString().split("T")[0];
              if (day > todayStr) {
                counts[day].breakfast += 1;
                counts[day].lunch += 1;
                counts[day].supper += 1;
              }
            }
          });
        });

        setAllUsers(all);
        setMealCounts(counts);
      } catch (err) {
        console.error("Admin data error:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchAll();
  }, []);

  const updateUserStatus = async (email, newStatus) => {
    try {
      const usersRef = collection(db, "users");
      const q = query(usersRef, where("email", "==", email));
      const snapshot = await getDocs(q);

      snapshot.forEach(async (docSnap) => {
        await updateDoc(doc(db, "users", docSnap.id), { status: newStatus });
      });

      alert(`‚úÖ User ${email} marked as ${newStatus}`);
      setAllUsers((prev) =>
        prev.map((u) => (u.email === email ? { ...u, status: newStatus } : u))
      );
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  const generateReport = () => {
    if (!fromDate || !toDate) {
      alert("‚ö†Ô∏è Please select both From and To dates!");
      return;
    }

    const points = {};
    const todayStr = new Date().toISOString().split("T")[0];

    // month name ‚Üí index
    const monthIndex = [
      "January","February","March","April","May","June",
      "July","August","September","October","November","December"
    ].indexOf(month);

    const start = new Date(year, monthIndex, fromDate);
    const end = new Date(year, monthIndex, toDate);

    start.setHours(0, 0, 0, 0);
    end.setHours(0, 0, 0, 0);

    allUsers.forEach((user) => {
      if (user.status && user.status !== "approved") return;

      const email = user.email || "Unknown";
      const userMeals = user.meals || {};
      let totalPoints = 0;

      const mealDates = Object.keys(userMeals).sort();
      const firstMealDate = mealDates.length > 0 ? mealDates[0] : todayStr;

      for (
        let d = new Date(start.getTime());
        d.getTime() <= end.getTime();
        d.setDate(d.getDate() + 1)
      ) {
        const day = d.toISOString().split("T")[0];

        let meals = userMeals[day];
        if (!meals) {
          if (day > todayStr) {
            meals = { breakfast: true, lunch: true, supper: true };
          } else if (day >= firstMealDate) {
            meals = { breakfast: true, lunch: true, supper: true };
          } else {
            continue;
          }
        }

        const marked = {
          B: !!meals.breakfast,
          L: !!meals.lunch,
          S: !!meals.supper,
        };
        const markedArr = ["B", "L", "S"].filter((m) => marked[m]);

        let key;
        if (markedArr.length === 3) key = "X";
        else if (markedArr.length === 0) key = "0";
        else key = markedArr.join("/");

        totalPoints += pointSystem[key] ?? 0;
      }

      points[email] = totalPoints.toFixed(2);
    });

    setUserPoints(points);
    setGenerated(true);
  };

  const exportCSV = () => {
    const rows = [["Email", "Points"]];
    Object.entries(userPoints).forEach(([email, pts]) => {
      rows.push([email, pts]);
    });

    const csvContent = rows.map((r) => r.join(",")).join("\n");
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });

    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.setAttribute(
      "download",
      `points_${month}_${fromDate}_to_${toDate}_${year}.csv`
    );
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  if (loading) return <h2 className="loading">Loading data...</h2>;

  // Points report page
  if (view === "points") {
    return (
      <div className="admin-container">
        <h2 className="title">üìë Generate Points Report</h2>
        <div className="report-filters">
          {/* Month Selector */}
          <select value={month} onChange={(e) => setMonth(e.target.value)}>
            {[
              "January","February","March","April","May","June",
              "July","August","September","October","November","December"
            ].map((m) => (
              <option key={m} value={m}>{m}</option>
            ))}
          </select>

          {/* From Day */}
          <select value={fromDate} onChange={(e) => setFromDate(Number(e.target.value))}>
            {Array.from({ length: 31 }, (_, i) => (
              <option key={i + 1} value={i + 1}>{i + 1}</option>
            ))}
          </select>

          {/* To Day */}
          <select value={toDate} onChange={(e) => setToDate(Number(e.target.value))}>
            {Array.from({ length: 31 }, (_, i) => (
              <option key={i + 1} value={i + 1}>{i + 1}</option>
            ))}
          </select>

          {/* Year Selector */}
          <select value={year} onChange={(e) => setYear(Number(e.target.value))}>
            {Array.from({ length: 6 }, (_, i) => {
              const y = 2023 + i;
              return <option key={y} value={y}>{y}</option>;
            })}
          </select>

          <button className="btn generate-btn" onClick={generateReport}>
            Generate
          </button>
        </div>
        {generated && (
          <>
            <table className="meal-table">
              <thead>
                <tr><th>Email</th><th>Total Points</th></tr>
              </thead>
              <tbody>
                {Object.entries(userPoints).map(([email, points]) => (
                  <tr key={email}><td>{email}</td><td>{points}</td></tr>
                ))}
              </tbody>
            </table>
            <button className="btn export-btn" onClick={exportCSV}>
              ‚¨áÔ∏è Download CSV
            </button>
          </>
        )}
        <div className="admin-actions">
          <button className="btn" onClick={() => setView("dashboard")}>
            ‚¨ÖÔ∏è Back
          </button>
        </div>
      </div>
    );
  }

  // Requests + Meals + Dashboard remain same‚Ä¶
  // (kept unchanged from your code above)

  const todayStr = new Date().toISOString().split("T")[0];
  const todayMeals = mealCounts[todayStr] || { breakfast: 0, lunch: 0, supper: 0 };

  return (
    <div className="admin-container">
      <h2 className="title">üìä Admin Dashboard</h2>

      <h3>üçΩÔ∏è Today‚Äôs Meal Count</h3>
      <div className="today-meals">
        <p>üç≥ Breakfast: <strong>{todayMeals.breakfast}</strong></p>
        <p>ü•ó Lunch: <strong>{todayMeals.lunch}</strong></p>
        <p>üçõ Supper: <strong>{todayMeals.supper}</strong></p>
      </div>

      <div className="admin-actions">
        <button className="btn points-btn" onClick={goToPointSystem}>‚öôÔ∏è Point System</button>
        <button className="btn" onClick={() => setView("points")}>üìë Points Report</button>
        <button className="btn" onClick={() => setView("requests")}>üë• User Requests</button>
        <button className="btn" onClick={() => setView("meals")}>ü•ò Meal Counts</button>
        <button className="logout-btn" onClick={onLogout}>Logout</button>
      </div>
    </div>
  );
}

