import { useState, useEffect } from "react";
import { db, auth, provider } from "./firebase";
import { signInWithPopup, signOut } from "firebase/auth";
import { doc, getDoc, setDoc, collection, getDocs } from "firebase/firestore";

// ðŸ‘‰ Add your admin emails here
const adminEmails = ["mohdnabeel2pc@gmail", "warden@hostel.com"];

function App() {
  const [user, setUser] = useState(null);
  const [meals, setMeals] = useState({
    breakfast: false,
    lunch: false,
    supper: false,
  });
  const [mealCounts, setMealCounts] = useState({
    breakfast: 0,
    lunch: 0,
    supper: 0,
  });
  const [isAdmin, setIsAdmin] = useState(false);

  // --- LOGIN ---
  const login = () => {
    signInWithPopup(auth, provider).then((result) => {
      setUser(result.user);
      setIsAdmin(adminEmails.includes(result.user.email));
      if (!adminEmails.includes(result.user.email)) {
        loadUserMeals(result.user.uid);
      } else {
        fetchMealCounts();
      }
    });
  };

  const logout = () => {
    signOut(auth).then(() => {
      setUser(null);
      setIsAdmin(false);
      setMeals({ breakfast: false, lunch: false, supper: false });
      setMealCounts({ breakfast: 0, lunch: 0, supper: 0 });
    });
  };

  // --- STUDENT: Load meals ---
  const loadUserMeals = async (uid) => {
    const userRef = doc(db, "users", uid);
    const snap = await getDoc(userRef);
    if (snap.exists()) {
      setMeals(snap.data().meals || {});
    }
  };

  // --- STUDENT: Toggle meal ---
  const toggleMeal = async (meal) => {
    if (!user) return;

    const newMeals = { ...meals, [meal]: !meals[meal] };
    setMeals(newMeals);

    const userRef = doc(db, "users", user.uid);
    await setDoc(userRef, { meals: newMeals }, { merge: true });
  };

  // --- ADMIN: Count meals ---
  const fetchMealCounts = async () => {
    const usersRef = collection(db, "users");
    const snapshot = await getDocs(usersRef);

    let counts = { breakfast: 0, lunch: 0, supper: 0 };
    snapshot.forEach((doc) => {
      const data = doc.data();
      if (data.meals) {
        if (data.meals.breakfast) counts.breakfast++;
        if (data.meals.lunch) counts.lunch++;
        if (data.meals.supper) counts.supper++;
      }
    });

    setMealCounts(counts);
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>Mess App</h1>

      {!user ? (
        <button onClick={login}>Login with Google</button>
      ) : (
        <div>
          <p>Welcome, {user.displayName}</p>
          <button onClick={logout}>Logout</button>

          {isAdmin ? (
            <div>
              <h2>ðŸ“Š Admin Dashboard</h2>
              <button onClick={fetchMealCounts}>ðŸ”„ Refresh Counts</button>
              <p>Breakfast: {mealCounts.breakfast}</p>
              <p>Lunch: {mealCounts.lunch}</p>
              <p>Supper: {mealCounts.supper}</p>
            </div>
          ) : (
            <div>
              <h2>Mark Your Meals</h2>
              <button
                style={{
                  background: meals.breakfast ? "green" : "lightgray",
                  margin: "5px",
                }}
                onClick={() => toggleMeal("breakfast")}
              >
                Breakfast {meals.breakfast ? "âœ…" : ""}
              </button>

              <button
                style={{
                  background: meals.lunch ? "green" : "lightgray",
                  margin: "5px",
                }}
                onClick={() => toggleMeal("lunch")}
              >
                Lunch {meals.lunch ? "âœ…" : ""}
              </button>

              <button
                style={{
                  background: meals.supper ? "green" : "lightgray",
                  margin: "5px",
                }}
                onClick={() => toggleMeal("supper")}
              >
                Supper {meals.supper ? "âœ…" : ""}
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
