import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { collection, getDocs, doc, getDoc, setDoc, updateDoc, query, where } from "firebase/firestore";
import "./AdminPage.css";

function AdminPage({ onLogout, goToPointSystem }) {
  const [mealCounts, setMealCounts] = useState({});
  const [userPoints, setUserPoints] = useState({});
  const [loading, setLoading] = useState(true);
  const [allUsers, setAllUsers] = useState([]);

  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [generated, setGenerated] = useState(false);

  const [view, setView] = useState("dashboard"); // 🔹 switch between dashboard / requests

  const defaultSystem = { X: 1, "0": 0, B: 0.25, L: 0.35, S: 0.45, "B/L": 0.65, "B/S": 0.75, "L/S": 0.85 };
  const [pointSystem, setPointSystem] = useState(defaultSystem);

  // Load users + settings
  useEffect(() => {
    const fetchAll = async () => {
      try {
        const settingsRef = doc(db, "settings", "points");
        const settingsSnap = await getDoc(settingsRef);
        if (settingsSnap.exists()) setPointSystem(settingsSnap.data());
        else await setDoc(settingsRef, defaultSystem);

        const usersRef = collection(db, "users");
        const querySnapshot = await getDocs(usersRef);

        const all = [];
        querySnapshot.forEach((docSnap) => all.push(docSnap.data()));
        setAllUsers(all);
      } catch (err) {
        console.error("Admin data error:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchAll();
  }, []);

  // Approve / Reject function
  const updateUserStatus = async (email, newStatus) => {
    try {
      const usersRef = collection(db, "users");
      const q = query(usersRef, where("email", "==", email));
      const snapshot = await getDocs(q);

      snapshot.forEach(async (docSnap) => {
        await updateDoc(doc(db, "users", docSnap.id), { status: newStatus });
      });

      alert(`✅ User ${email} marked as ${newStatus}`);
      window.location.reload();
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  if (loading) return <h2 className="loading">Loading data...</h2>;

  // 🔹 Requests View
  if (view === "requests") {
    return (
      <div className="admin-container">
        <h2 className="title">🆕 Pending User Requests</h2>
        <table className="meal-table">
          <thead>
            <tr>
              <th>Name</th><th>Email</th><th>Year</th><th>Branch</th><th>Phone</th><th>Hostel</th><th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {allUsers.filter((u) => u.status === "pending").map((user) => (
              <tr key={user.email}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.year}</td>
                <td>{user.branch}</td>
                <td>{user.phone}</td>
                <td>{user.mea}</td>
                <td>
                  <button className="btn approve-btn" onClick={() => updateUserStatus(user.email, "approved")}>✅ Approve</button>
                  <button className="btn reject-btn" onClick={() => updateUserStatus(user.email, "rejected")}>❌ Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="admin-actions">
          <button className="btn" onClick={() => setView("dashboard")}>⬅️ Back</button>
          <button className="logout-btn" onClick={onLogout}>Logout</button>
        </div>
      </div>
    );
  }

  // 🔹 Dashboard View
  return (
    <div className="admin-container">
      <h2 className="title">📊 Admin Dashboard</h2>

      {/* Existing dashboard things here … */}

      <div className="admin-actions">
        <button className="btn points-btn" onClick={goToPointSystem}>⚙️ Point System</button>
        <button className="btn" onClick={() => setView("requests")}>👥 User Requests</button>
        <button className="logout-btn" onClick={onLogout}>Logout</button>
      </div>
    </div>
  );
}

export default AdminPage;
