import React, { useState } from "react";

function App() {
  // Track which meals are marked
  const [meals, setMeals] = useState({
    breakfast: false,
    lunch: false,
    supper: false,
  });

  // Toggle meal marking
  const toggleMeal = (meal) => {
    setMeals((prev) => ({
      ...prev,
      [meal]: !prev[meal], // flip true/false
    }));
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Mess App</h1>
      <p>Mark your meals below (toggle on/off)</p>

      <div style={{ marginTop: "20px" }}>
        <button
          onClick={() => toggleMeal("breakfast")}
          style={{
            margin: "10px",
            padding: "10px 20px",
            background: meals.breakfast ? "green" : "gray",
            color: "white",
            border: "none",
            borderRadius: "5px",
          }}
        >
          {meals.breakfast ? "✓ Breakfast Marked" : "Mark Breakfast"}
        </button>

        <button
          onClick={() => toggleMeal("lunch")}
          style={{
            margin: "10px",
            padding: "10px 20px",
            background: meals.lunch ? "green" : "gray",
            color: "white",
            border: "none",
            borderRadius: "5px",
          }}
        >
          {meals.lunch ? "✓ Lunch Marked" : "Mark Lunch"}
        </button>

        <button
          onClick={() => toggleMeal("supper")}
          style={{
            margin: "10px",
            padding: "10px 20px",
            background: meals.supper ? "green" : "gray",
            color: "white",
            border: "none",
            borderRadius: "5px",
          }}
        >
          {meals.supper ? "✓ Supper Marked" : "Mark Supper"}
        </button>
      </div>
    </div>
  );
}

export default App;
