import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { doc, onSnapshot } from "firebase/firestore";

function AdminPage() {
  const [mealCounts, setMealCounts] = useState({
    breakfast: 0,
    lunch: 0,
    supper: 0,
  });

  useEffect(() => {
    const meals = ["breakfast", "lunch", "supper"];
    const unsubscribers = meals.map((meal) =>
      onSnapshot(doc(db, "meals", meal), (docSnap) => {
        setMealCounts((prev) => ({
          ...prev,
          [meal]: docSnap.exists() ? (docSnap.data().users?.length || 0) : 0,
        }));
      })
    );

    return () => unsubscribers.forEach((unsub) => unsub());
  }, []);

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>üìä Admin Dashboard</h2>
      <h3>Live Meal Counts</h3>
      <p>üç≥ Breakfast: {mealCounts.breakfast}</p>
      <p>üçõ Lunch: {mealCounts.lunch}</p>
      <p>üç≤ Supper: {mealCounts.supper}</p>
    </div>
  );
}

export default AdminPage;
