// AdminPage.js
import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import {
  collection,
  getDocs,
  doc,
  getDoc,
  setDoc,
  updateDoc,
  query,
  where,
} from "firebase/firestore";
import "./AdminPage.css";

function AdminPage({ onLogout, goToPointSystem }) {
  const [mealCounts, setMealCounts] = useState({});
  const [userPoints, setUserPoints] = useState({});
  const [loading, setLoading] = useState(true);

  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [generated, setGenerated] = useState(false);

  const [allUsers, setAllUsers] = useState([]);
  const [view, setView] = useState("dashboard"); // dashboard | requests | points | meals
const [month, setMonth] = useState(new Date().getMonth() + 1);
const [year, setYear] = useState(new Date().getFullYear());
const [fromDay, setFromDay] = useState(1);
const [toDay, setToDay] = useState(1);



  const defaultSystem = {
    X: 1,
    "0": 0,
    B: 0.25,
    L: 0.35,
    S: 0.45,
    "B/L": 0.65,
    "B/S": 0.75,
    "L/S": 0.85,
  };
  const [pointSystem, setPointSystem] = useState(defaultSystem);

  // --- move todayStr here so all views can use it safely ---
  const todayStr = new Date().toISOString().split("T")[0];

  // fetch settings + users + meal counts
  useEffect(() => {
    const fetchAll = async () => {
      try {
        const settingsRef = doc(db, "settings", "points");
        const settingsSnap = await getDoc(settingsRef);
        if (settingsSnap.exists()) {
          setPointSystem(settingsSnap.data());
        } else {
          await setDoc(settingsRef, defaultSystem);
        }

        const usersRef = collection(db, "users");
        const querySnapshot = await getDocs(usersRef);

        const all = [];
        const counts = {};
        const today = new Date();

        const nextDays = [];
        for (let i = 0; i < 8; i++) {
          const d = new Date(today);
          d.setDate(today.getDate() + i);
          const dateStr = d.toISOString().split("T")[0];
          nextDays.push(dateStr);
          counts[dateStr] = { breakfast: 0, lunch: 0, supper: 0 };
        }

        querySnapshot.forEach((docSnap) => {
          const data = docSnap.data();
          all.push(data);

          const userMeals = data.meals || {};
          nextDays.forEach((day) => {
            const meals = userMeals[day];
            if (meals) {
              ["breakfast", "lunch", "supper"].forEach((meal) => {
                if (meals[meal]) counts[day][meal] += 1;
              });
            } else {
              // local todayStr inside fetch is okay but we already have outer todayStr
              if (day > todayStr) {
                counts[day].breakfast += 1;
                counts[day].lunch += 1;
                counts[day].supper += 1;
              }
            }
          });
        });

        setAllUsers(all);
        setMealCounts(counts);
      } catch (err) {
        console.error("Admin data error:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchAll();
  }, []);

  const updateUserStatus = async (email, newStatus) => {
    try {
      const usersRef = collection(db, "users");
      const q = query(usersRef, where("email", "==", email));
      const snapshot = await getDocs(q);

      snapshot.forEach(async (docSnap) => {
        await updateDoc(doc(db, "users", docSnap.id), { status: newStatus });
      });

      alert(`✅ User ${email} marked as ${newStatus}`);
      setAllUsers((prev) =>
        prev.map((u) => (u.email === email ? { ...u, status: newStatus } : u))
      );
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  const generateReport = (f, t) => {
  const from = f || fromDate;
  const to = t || toDate;

  if (!from || !to) {
    alert("⚠️ Please select both From and To dates!");
    return;
  }

  const points = {};

  allUsers.forEach((user) => {
    if (user.status && user.status !== "approved") return;

    const email = user.email || "Unknown";
    const userMeals = user.meals || {};
    let totalPoints = 0;

    const mealDates = Object.keys(userMeals).sort();
    const firstMealDate = mealDates.length > 0 ? mealDates[0] : todayStr;

    const start = new Date(from);
    const end = new Date(to);
    start.setHours(0, 0, 0, 0);
    end.setHours(0, 0, 0, 0);

    for (
      let d = new Date(start.getTime());
      d.getTime() <= end.getTime();
      d.setDate(d.getDate() + 1)
    ) {
      const day = d.toISOString().split("T")[0];

      let meals = userMeals[day];
      if (!meals) {
        if (day > todayStr) {
          meals = { breakfast: true, lunch: true, supper: true };
        } else if (day >= firstMealDate) {
          meals = { breakfast: true, lunch: true, supper: true };
        } else {
          continue;
        }
      }

      const marked = {
        B: !!meals.breakfast,
        L: !!meals.lunch,
        S: !!meals.supper,
      };
      const markedArr = ["B", "L", "S"].filter((m) => marked[m]);

      let key;
      if (markedArr.length === 3) key = "X";
      else if (markedArr.length === 0) key = "0";
      else key = markedArr.join("/");

      totalPoints += pointSystem[key] ?? 0;
    }

    points[email] = totalPoints.toFixed(2);
  });

  setUserPoints(points);
  setGenerated(true);
};

  const exportCSV = () => {
    const rows = [["Email", "Points"]];
    Object.entries(userPoints).forEach(([email, pts]) => {
      rows.push([email, pts]);
    });

    const csvContent = rows.map((r) => r.join(",")).join("\n");
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });

    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.setAttribute("download", `points_${fromDate}_to_${toDate}.csv`);
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  if (loading) return <h2 className="loading">Loading data...</h2>;

  // Requests page
  if (view === "requests") {
    return (
      <div className="admin-container">
        <h2 className="title">🆕 Pending User Requests</h2>
        <table className="meal-table">
          <thead>
            <tr>
              <th>Name</th><th>Email</th><th>Year</th><th>Branch</th><th>Phone</th><th>Hostel</th><th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {allUsers.filter((u) => u.status === "pending").map((user) => (
              <tr key={user.email}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.year}</td>
                <td>{user.branch}</td>
                <td>{user.phone}</td>
                <td>{user.mea}</td>
                <td>
                  <button className="btn approve-btn" onClick={() => updateUserStatus(user.email, "approved")}>✅ Approve</button>
                  <button className="btn reject-btn" onClick={() => updateUserStatus(user.email, "rejected")}>❌ Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="admin-actions">
          <button className="btn" onClick={() => setView("dashboard")}>⬅️ Back</button>
          <button className="logout-btn" onClick={onLogout}>Logout</button>
        </div>
      </div>
    );
  }

  // Points report page
// Points report page
if (view === "points") {
  return (
    <div className="admin-container">
      <h2 className="title">📑 Generate Points Report</h2>

      <div className="report-filters">
        {/* Month Selector */}
        <label>
          Month:
          <select value={month} onChange={(e) => setMonth(Number(e.target.value))}>
            {[
              "January","February","March","April","May","June",
              "July","August","September","October","November","December"
            ].map((m, i) => (
              <option key={i} value={i+1}>{m}</option>
            ))}
          </select>
        </label>

        {/* Year Selector */}
        <label>
          Year:
          <select value={year} onChange={(e) => setYear(Number(e.target.value))}>
            {Array.from({length: 6}, (_,i) => 2023+i).map((y) => (
              <option key={y} value={y}>{y}</option>
            ))}
          </select>
        </label>

        {/* From & To days */}
        <label>
          From:
          <select value={fromDay} onChange={(e) => setFromDay(Number(e.target.value))}>
            {Array.from({length: 31}, (_,i) => i+1).map((d) => (
              <option key={d} value={d}>{d}</option>
            ))}
          </select>
        </label>

        <label>
          To:
          <select value={toDay} onChange={(e) => setToDay(Number(e.target.value))}>
            {Array.from({length: 31}, (_,i) => i+1).map((d) => (
              <option key={d} value={d}>{d}</option>
            ))}
          </select>
        </label>

        <button
  className="btn generate-btn"
  onClick={() => {
    const f = `${year}-${String(month).padStart(2,"0")}-${String(fromDay).padStart(2,"0")}`;
    const t = `${year}-${String(month).padStart(2,"0")}-${String(toDay).padStart(2,"0")}`;
    setFromDate(f);
    setToDate(t);
    generateReport(f, t);   // ✅ use directly
  }}
>
  Generate
</button>

      </div>

      {generated && (
        <>
          <table className="meal-table">
            <thead>
              <tr><th>Email</th><th>Total Points</th></tr>
            </thead>
            <tbody>
              {Object.entries(userPoints).map(([email, points]) => (
                <tr key={email}><td>{email}</td><td>{points}</td></tr>
              ))}
            </tbody>
          </table>
          <button className="btn export-btn" onClick={exportCSV}>⬇️ Download CSV</button>
        </>
      )}
      <div className="admin-actions">
        <button className="btn" onClick={() => setView("dashboard")}>⬅️ Back</button>
      </div>
    </div>
  );
}

  // Meals page
  if (view === "meals") {
    return (
      <div className="admin-container">
        <h2 className="title">🥘 Meal Counts (Next 8 Days)</h2>
        <table className="meal-table">
          <thead>
            <tr><th>Date</th><th>Breakfast</th><th>Lunch</th><th>Supper</th></tr>
          </thead>
          <tbody>
            {Object.entries(mealCounts).map(([day, meals]) => (
              <tr key={day}>
                <td className="date-col">{day}</td>
                <td className={day === todayStr ? "today-col" : ""}>{meals.breakfast}</td>
                <td className={day === todayStr ? "today-col" : ""}>{meals.lunch}</td>
                <td className={day === todayStr ? "today-col" : ""}>{meals.supper}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="admin-actions">
          <button className="btn" onClick={() => setView("dashboard")}>⬅️ Back</button>
        </div>
      </div>
    );
  }

  // Dashboard
  const todayMeals = mealCounts[todayStr] || { breakfast: 0, lunch: 0, supper: 0 };

  return (
    <div className="admin-container">
      <h2 className="title">📊 Admin Dashboard</h2>

      <h3>🍽️ Today’s Meal Count</h3>
      <div className="today-meals">
        <p>🍳 Breakfast: <strong>{todayMeals.breakfast}</strong></p>
        <p>🥗 Lunch: <strong>{todayMeals.lunch}</strong></p>
        <p>🍛 Supper: <strong>{todayMeals.supper}</strong></p>
      </div>

      <div className="admin-actions">
        <button className="btn points-btn" onClick={goToPointSystem}>⚙️ Point System</button>
        <button className="btn" onClick={() => setView("points")}>📑 Points Report</button>
        <button className="btn" onClick={() => setView("requests")}>👥 User Requests</button>
        <button className="btn" onClick={() => setView("meals")}>🥘 Meal Counts</button>
        <button className="logout-btn" onClick={onLogout}>Logout</button>
      </div>
    </div>
  );
}

export default AdminPage;
