import React, { useEffect, useState } from "react";
import { auth, db } from "./firebase";
import { signOut } from "firebase/auth";
import { doc, getDoc, setDoc, updateDoc, collection, getDocs } from "firebase/firestore";

export default function Dashboard() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState("student");
  const [meals, setMeals] = useState({ breakfast: false, lunch: false, supper: false });
  const [totals, setTotals] = useState({ breakfast: 0, lunch: 0, supper: 0 });

  useEffect(() => {
    const currentUser = auth.currentUser;
    if (!currentUser) return;

    const userRef = doc(db, "users", currentUser.uid);

    async function fetchUserData() {
      const userSnap = await getDoc(userRef);

      if (!userSnap.exists()) {
        // New user → set default role = student + default meals
        await setDoc(userRef, {
          name: currentUser.displayName,
          email: currentUser.email,
          role: "student",
          meals: { breakfast: false, lunch: false, supper: false }
        });
        setRole("student");
        setMeals({ breakfast: false, lunch: false, supper: false });
      } else {
        const data = userSnap.data();
        setRole(data.role || "student");
        setMeals(data.meals || { breakfast: false, lunch: false, supper: false });
      }
      setUser(currentUser);
    }

    fetchUserData();
  }, []);

  // For admin → fetch totals
  useEffect(() => {
    if (role !== "admin") return;

    async function fetchTotals() {
      const querySnapshot = await getDocs(collection(db, "users"));
      let counts = { breakfast: 0, lunch: 0, supper: 0 };

      querySnapshot.forEach((doc) => {
        const data = doc.data();
        if (data.meals) {
          if (data.meals.breakfast) counts.breakfast += 1;
          if (data.meals.lunch) counts.lunch += 1;
          if (data.meals.supper) counts.supper += 1;
        }
      });

      setTotals(counts);
    }

    fetchTotals();
  }, [role, meals]); // refresh totals when role or meals change

  const toggleMeal = async (meal) => {
    const currentUser = auth.currentUser;
    if (!currentUser) return;

    const userRef = doc(db, "users", currentUser.uid);
    const newMeals = { ...meals, [meal]: !meals[meal] };
    setMeals(newMeals);

    await updateDoc(userRef, { meals: newMeals });
  };

  const handleLogout = () => {
    signOut(auth);
  };

  if (!user) return <h2>Loading...</h2>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>Welcome, {user.displayName}</h1>
      <p>Role: {role}</p>

      {role === "student" ? (
        <>
          <h2>Your Meals</h2>
          <div>
            <button onClick={() => toggleMeal("breakfast")}>
              Breakfast: {meals.breakfast ? "✅" : "❌"}
            </button>
            <button onClick={() => toggleMeal("lunch")}>
              Lunch: {meals.lunch ? "✅" : "❌"}
            </button>
            <button onClick={() => toggleMeal("supper")}>
              Supper: {meals.supper ? "✅" : "❌"}
            </button>
          </div>
        </>
      ) : (
        <>
          <h2>Meal Counts (All Users)</h2>
          <p>🍳 Breakfast: {totals.breakfast}</p>
          <p>🍛 Lunch: {totals.lunch}</p>
          <p>🍲 Supper: {totals.supper}</p>
        </>
      )}

      <br />
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
}
