import React, { useState, useEffect } from "react";
import {
  auth,
  provider,
  signInWithPopup,
  signOut,
  db,
} from "./firebase";
import {
  doc,
  setDoc,
  getDoc,
  onSnapshot,
  collection,
} from "firebase/firestore";

function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState("student");
  const [meals, setMeals] = useState({ breakfast: false, lunch: false, supper: false });
  const [mealCounts, setMealCounts] = useState({ breakfast: 0, lunch: 0, supper: 0 });

  // Monitor login state
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(async (currentUser) => {
      if (currentUser) {
        setUser(currentUser);

        // Get role from Firestore
        const userRef = doc(db, "users", currentUser.uid);
        const userSnap = await getDoc(userRef);

        if (userSnap.exists()) {
          setRole(userSnap.data().role || "student");
          setMeals(userSnap.data().meals || { breakfast: false, lunch: false, supper: false });
        } else {
          // New user = student by default
          await setDoc(userRef, {
            name: currentUser.displayName,
            email: currentUser.email,
            role: "student",
            meals: { breakfast: false, lunch: false, supper: false },
          });
          setRole("student");
        }
      } else {
        setUser(null);
        setRole("student");
      }
    });
    return () => unsubscribe();
  }, []);

  // Live meal counts for admin
  useEffect(() => {
    if (role === "admin") {
      const unsubscribe = onSnapshot(collection(db, "users"), (snapshot) => {
        let counts = { breakfast: 0, lunch: 0, supper: 0 };
        snapshot.forEach((doc) => {
          const data = doc.data();
          if (data.meals?.breakfast) counts.breakfast++;
          if (data.meals?.lunch) counts.lunch++;
          if (data.meals?.supper) counts.supper++;
        });
        setMealCounts(counts);
      });
      return () => unsubscribe();
    }
  }, [role]);

  // Google login
  const handleLogin = async () => {
    try {
      await signInWithPopup(auth, provider);
    } catch (error) {
      console.error("Login error:", error);
    }
  };

  // Logout
  const handleLogout = async () => {
    await signOut(auth);
  };

  // Toggle meals (students)
  const toggleMeal = async (meal) => {
    const newMeals = { ...meals, [meal]: !meals[meal] };
    setMeals(newMeals);

    const userRef = doc(db, "users", user.uid);
    await setDoc(userRef, { meals: newMeals }, { merge: true });
  };

  // ===========================
  // UI
  // ===========================
  if (!user) {
    return (
      <div style={{ textAlign: "center", marginTop: "100px" }}>
        <h1>Mess App</h1>
        <button onClick={handleLogin}>Login with Google</button>
      </div>
    );
  }

  return (
    <div style={{ padding: "20px", textAlign: "center" }}>
      <h2>Welcome, {user.displayName}</h2>
      <button onClick={handleLogout}>Logout</button>

      {role === "student" && (
        <div style={{ marginTop: "30px" }}>
          <h3>Mark Your Meals</h3>
          {["breakfast", "lunch", "supper"].map((meal) => (
            <div key={meal}>
              <button
                onClick={() => toggleMeal(meal)}
                style={{
                  backgroundColor: meals[meal] ? "green" : "gray",
                  color: "white",
                  padding: "10px",
                  margin: "5px",
                  border: "none",
                  borderRadius: "8px",
                  cursor: "pointer",
                }}
              >
                {meal} {meals[meal] ? "âœ”" : ""}
              </button>
            </div>
          ))}
        </div>
      )}

      {role === "admin" && (
        <div style={{ marginTop: "30px" }}>
          <h3>Admin Dashboard - Meal Counts</h3>
          <p>Breakfast: {mealCounts.breakfast}</p>
          <p>Lunch: {mealCounts.lunch}</p>
          <p>Supper: {mealCounts.supper}</p>
        </div>
      )}
    </div>
  );
}

export default App;
