// AdminPage.js
import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import {
  collection,
  getDocs,
  doc,
  getDoc,
  setDoc,
  updateDoc,
  query,
  where,
} from "firebase/firestore";
import "./AdminPage.css";

function AdminPage({ onLogout, goToPointSystem }) {
  const [mealCounts, setMealCounts] = useState({});
  const [userPoints, setUserPoints] = useState({});
  const [userMarks, setUserMarks] = useState({}); // day-by-day markings
  const [loading, setLoading] = useState(true);

  // range state (strings "YYYY-MM-DD")
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [generated, setGenerated] = useState(false);

  const [allUsers, setAllUsers] = useState([]);
  const [view, setView] = useState("dashboard"); // dashboard | requests | points | meals

  // Report filters (dropdowns)
  const [month, setMonth] = useState(new Date().getMonth() + 1);
  const [year, setYear] = useState(new Date().getFullYear());
  const [fromDay, setFromDay] = useState(1);
  const [toDay, setToDay] = useState(1);

  // Single-day export selector (inside points view)
  const [dailyDay, setDailyDay] = useState(new Date().getDate());

  const defaultSystem = {
    X: 1,
    "0": 0,
    B: 0.25,
    L: 0.35,
    S: 0.45,
    "B/L": 0.65,
    "B/S": 0.75,
    "L/S": 0.85,
  };
  const [pointSystem, setPointSystem] = useState(defaultSystem);

  // last generated range (used by export if needed)
  const [generatedRange, setGeneratedRange] = useState({ from: "", to: "" });

  const todayStr = new Date().toISOString().split("T")[0];

  // --- fetch settings + users + meal counts ---
  useEffect(() => {
    const fetchAll = async () => {
      try {
        const settingsRef = doc(db, "settings", "points");
        const settingsSnap = await getDoc(settingsRef);
        if (settingsSnap.exists()) {
          setPointSystem(settingsSnap.data());
        } else {
          await setDoc(settingsRef, defaultSystem);
        }

        const usersRef = collection(db, "users");
        const querySnapshot = await getDocs(usersRef);

        const all = [];
        const counts = {};
        const today = new Date();

        const nextDays = [];
        for (let i = 0; i < 8; i++) {
          const d = new Date(today);
          d.setDate(today.getDate() + i);
          const dateStr = d.toISOString().split("T")[0];
          nextDays.push(dateStr);
          counts[dateStr] = { breakfast: 0, lunch: 0, supper: 0 };
        }

        querySnapshot.forEach((docSnap) => {
          const data = docSnap.data();
          all.push(data);

          const userMeals = data.meals || {};
          nextDays.forEach((day) => {
            const meals = userMeals[day];
            if (meals) {
              ["breakfast", "lunch", "supper"].forEach((meal) => {
                if (meals[meal]) counts[day][meal] += 1;
              });
            } else {
              if (day > todayStr) {
                counts[day].breakfast += 1;
                counts[day].lunch += 1;
                counts[day].supper += 1;
              }
            }
          });
        });

        setAllUsers(all);
        setMealCounts(counts);
      } catch (err) {
        console.error("Admin data error:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchAll();
  }, []); // run once

  // --- update user status (approve/reject) ---
  const updateUserStatus = async (email, newStatus) => {
    try {
      const usersRef = collection(db, "users");
      const q = query(usersRef, where("email", "==", email));
      const snapshot = await getDocs(q);

      snapshot.forEach(async (docSnap) => {
        await updateDoc(doc(db, "users", docSnap.id), { status: newStatus });
      });

      alert(`‚úÖ User ${email} marked as ${newStatus}`);
      setAllUsers((prev) =>
        prev.map((u) => (u.email === email ? { ...u, status: newStatus } : u))
      );
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  // --- helpers ---
  const buildDaysArray = (from, to) => {
    const arr = [];
    const s = new Date(from);
    const e = new Date(to);
    for (let d = new Date(s.getTime()); d.getTime() <= e.getTime(); d.setDate(d.getDate() + 1)) {
      arr.push(d.toISOString().split("T")[0]);
    }
    return arr;
  };

  // --- Generate report: accepts optional from/to (YYYY-MM-DD) to avoid double-click issues ---
  const generateReport = (f, t) => {
    const from = f || fromDate;
    const to = t || toDate;

    if (!from || !to) {
      alert("‚ö†Ô∏è Please select both From and To dates!");
      return;
    }

    // Prepare result containers
    const points = {};
    const markings = {};

    // Build list of days (so we can ensure each user gets an entry for every day)
    const days = buildDaysArray(from, to);

    allUsers.forEach((user) => {
      // allow approved OR old users (no status field)
      if (user.status && user.status !== "approved") return;

      const email = user.email || "Unknown";
      const displayName = user.name || email;
      const userMeals = user.meals || {};
      let totalPoints = 0;
      const userMarkings = {};

      // determine earliest meal date (for fallback logic)
      const mealDates = Object.keys(userMeals).sort();
      const firstMealDate = mealDates.length > 0 ? mealDates[0] : todayStr;

      // iterate each day in requested range
      days.forEach((day) => {
        let meals = userMeals[day];
        if (!meals) {
          // future days: assume all meals
          if (day > todayStr) {
            meals = { breakfast: true, lunch: true, supper: true };
          } else if (day >= firstMealDate) {
            // if user has any meals from firstMealDate onwards, assume they took meals for missing dates
            meals = { breakfast: true, lunch: true, supper: true };
          } else {
            // user had no registration/meals yet ‚Äî mark as 0
            userMarkings[day] = "0";
            return;
          }
        }

        // build marking code
        const markedArr = [];
        if (meals.breakfast) markedArr.push("B");
        if (meals.lunch) markedArr.push("L");
        if (meals.supper) markedArr.push("S");

        let key;
        if (markedArr.length === 3) key = "X";
        else if (markedArr.length === 0) key = "0";
        else key = markedArr.join("/");

        // add points according to pointSystem (supports combos)
        totalPoints += pointSystem[key] ?? 0;

        userMarkings[day] = key;
      });

      points[displayName] = totalPoints.toFixed(2);
      markings[displayName] = userMarkings;
    });

    // Save results and generated range
    setUserPoints(points);
    setUserMarks(markings);
    setGenerated(true);
    setFromDate(from);
    setToDate(to);
    setGeneratedRange({ from, to });
  };

  // --- Export CSV (range) with day-by-day markings + total points ---
  const exportCSV = () => {
    const from = generatedRange.from || fromDate;
    const to = generatedRange.to || toDate;

    if (!from || !to) {
      alert("‚ö†Ô∏è Please generate a report first (use Generate).");
      return;
    }

    const days = buildDaysArray(from, to);

    const header = ["Name", ...days, "Total Points"];
    const rows = [header];

    Object.entries(userPoints).forEach(([name, pts]) => {
      const marks = userMarks[name] || {};
      const row = [name];
      days.forEach((day) => {
        row.push(marks[day] || "0");
      });
      row.push(pts);
      rows.push(row);
    });

    const csvContent = rows.map((r) => r.join(",")).join("\n");
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });

    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.setAttribute("download", `points_${from}_to_${to}.csv`);
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  // --- Export single-day marking CSV (Name, Mark) for selected day in current month/year) ---
  const exportDailyMarkingCSV = () => {
    const day = String(dailyDay).padStart(2, "0");
    const mon = String(month).padStart(2, "0");
    const selectedDate = `${year}-${mon}-${day}`;

    const rows = [["Name", "Marking"]];

    allUsers.forEach((user) => {
      if (user.status && user.status !== "approved") return;

      const displayName = user.name || user.email || "Unknown";
      const meals = user.meals?.[selectedDate];

      let key;
      if (!meals) {
        if (selectedDate > todayStr) {
          key = "X";
        } else {
          // If user had no meals and no firstMealDate logic applied, show 0
          // Attempt fallback: if user has any meal on/after selectedDate's month assume X; but keep simple: 0
          key = "0";
        }
      } else {
        const arr = [];
        if (meals.breakfast) arr.push("B");
        if (meals.lunch) arr.push("L");
        if (meals.supper) arr.push("S");
        if (arr.length === 3) key = "X";
        else if (arr.length === 0) key = "0";
        else key = arr.join("/");
      }

      rows.push([displayName, key]);
    });

    const csvContent = rows.map((r) => r.join(",")).join("\n");
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });

    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.setAttribute("download", `daily_marking_${selectedDate}.csv`);
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  if (loading) return <h2 className="loading">Loading data...</h2>;

  // ---------- Requests view ----------
  if (view === "requests") {
    return (
      <div className="admin-container">
        <h2 className="title">üÜï Pending User Requests</h2>
        <table className="meal-table">
          <thead>
            <tr>
              <th>Name</th><th>Email</th><th>Year</th><th>Branch</th><th>Phone</th><th>Hostel</th><th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {allUsers.filter((u) => u.status === "pending").map((user) => (
              <tr key={user.email}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.year}</td>
                <td>{user.branch}</td>
                <td>{user.phone}</td>
                <td>{user.mea}</td>
                <td>
                  <button className="btn approve-btn" onClick={() => updateUserStatus(user.email, "approved")}>‚úÖ Approve</button>
                  <button className="btn reject-btn" onClick={() => updateUserStatus(user.email, "rejected")}>‚ùå Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="admin-actions">
          <button className="btn" onClick={() => setView("dashboard")}>‚¨ÖÔ∏è Back</button>
          <button className="logout-btn" onClick={onLogout}>Logout</button>
        </div>
      </div>
    );
  }

  // ---------- Points report view ----------
  if (view === "points") {
    // compute days in selected month for dropdown limits
    const daysInMonth = new Date(year, month, 0).getDate();

    return (
      <div className="admin-container">
        <h2 className="title">üìë Generate Points Report</h2>

        <div className="report-filters">
          <label>
            Month:
            <select value={month} onChange={(e) => setMonth(Number(e.target.value))}>
              {[
                "January","February","March","April","May","June",
                "July","August","September","October","November","December"
              ].map((m, i) => (
                <option key={i} value={i + 1}>{m}</option>
              ))}
            </select>
          </label>

          <label>
            Year:
            <select value={year} onChange={(e) => setYear(Number(e.target.value))}>
              {Array.from({ length: 6 }, (_, i) => 2023 + i).map((y) => (
                <option key={y} value={y}>{y}</option>
              ))}
            </select>
          </label>

          <label>
            From:
            <select value={fromDay} onChange={(e) => setFromDay(Number(e.target.value))}>
              {Array.from({ length: daysInMonth }, (_, i) => i + 1).map((d) => (
                <option key={d} value={d}>{d}</option>
              ))}
            </select>
          </label>

          <label>
            To:
            <select value={toDay} onChange={(e) => setToDay(Number(e.target.value))}>
              {Array.from({ length: daysInMonth }, (_, i) => i + 1).map((d) => (
                <option key={d} value={d}>{d}</option>
              ))}
            </select>
          </label>

          <button
            className="btn generate-btn"
            onClick={() => {
              // guard: if fromDay > toDay swap them
              let fDay = fromDay;
              let tDay = toDay;
              if (fromDay > toDay) {
                fDay = toDay;
                tDay = fromDay;
                setFromDay(fDay);
                setToDay(tDay);
              }
              const f = `${year}-${String(month).padStart(2, "0")}-${String(fDay).padStart(2, "0")}`;
              const t = `${year}-${String(month).padStart(2, "0")}-${String(tDay).padStart(2, "0")}`;
              // directly call generateReport with built dates (no double-click)
              generateReport(f, t);
            }}
          >
            Generate
          </button>
        </div>

        {generated && (
          <>
            <table className="meal-table">
              <thead>
                <tr><th>Name</th><th>Total Points</th></tr>
              </thead>
              <tbody>
                {Object.entries(userPoints).map(([name, points]) => (
                  <tr key={name}><td>{name}</td><td>{points}</td></tr>
                ))}
              </tbody>
            </table>

            <div style={{ display: "flex", gap: 8, alignItems: "center", marginTop: 10 }}>
              <button className="btn export-btn" onClick={exportCSV}>‚¨áÔ∏è Download Range CSV (with markings)</button>
            </div>
          </>
        )}

        {/* single-day daily marking export */}
        <div style={{ marginTop: 18, borderTop: "1px solid #ddd", paddingTop: 12 }}>
          <h3>üìÖ Export Single-Day Markings</h3>
          <label className="single-day">
            Day:
            <select value={dailyDay} onChange={(e) => setDailyDay(Number(e.target.value))}>
              {Array.from({ length: daysInMonth }, (_, i) => i + 1).map((d) => (
                <option key={d} value={d}>{d}</option>
              ))}
            </select>
          </label>
          <button className="btn export-btn" onClick={exportDailyMarkingCSV} style={{ marginLeft: 10 }}>
            ‚¨áÔ∏è Download todays Marking
          </button>
        </div>

        <div className="admin-actions" style={{ marginTop: 14 }}>
          <button className="btn" onClick={() => setView("dashboard")}>‚¨ÖÔ∏è Back</button>
        </div>
      </div>
    );
  }

  // ---------- Meals page ----------
  if (view === "meals") {
    return (
      <div className="admin-container">
        <h2 className="title">ü•ò Meal Counts (Next 8 Days)</h2>
        <table className="meal-table">
          <thead>
            <tr><th>Date</th><th>Breakfast</th><th>Lunch</th><th>Supper</th></tr>
          </thead>
          <tbody>
            {Object.entries(mealCounts).map(([day, meals]) => (
              <tr key={day}>
                <td className="date-col">{day}</td>
