import { useState } from "react";
import { auth, signInWithEmailAndPassword, sendPasswordResetEmail } from "./firebase";
import "./loginpage.css";

function LoginPage({ onLoginSuccess, goToSignup }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // Handle Login
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, password);
      onLoginSuccess();
    } catch (err) {
      alert("‚ö†Ô∏è " + err.message);
    }
  };

  // Handle Forgot Password
  const handleForgotPassword = async () => {
    if (!email) {
      alert("Please enter your email first!");
      return;
    }
    try {
      await sendPasswordResetEmail(auth, email);
      alert("üì© Password reset email sent!");
    } catch (err) {
      alert("‚ö†Ô∏è " + err.message);
    }
  };

  return (
    <div className="login-container">
      <form className="login-box" onSubmit={handleLogin}>
      <img src="/logo3.png" alt="Logo" className="lo-logo" />
      <h2 className="signup-title1">MEA MESS</h2>
      <h2 className="signup-title2">Login</h2>        
      <input
          type="email"
          className="input-field"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          className="input-field"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" className="btn">Login</button>

        <p className="link" onClick={handleForgotPassword}>Forgot Password?</p>
        <p className="link">
          Don‚Äôt have an account?{" "}
          <span onClick={goToSignup}>Sign up</span>
        </p>
      </form>
    </div>
  );
}

export default LoginPage;
