import React, { useState } from "react";

function App() {
  // Track which meals are marked
  const [meals, setMeals] = useState({
    breakfast: false,
    lunch: false,
    supper: false,
  });

  // Handle marking/unmarking meals (toggle)
  const handleMark = (meal) => {
    setMeals({ ...meals, [meal]: !meals[meal] });
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Mess App</h1>
      <p>Select your meals for today</p>

      <div>
        <button onClick={() => handleMark("breakfast")}>
          {meals.breakfast ? "✅ Breakfast Marked" : "Mark Breakfast"}
        </button>
      </div>

      <div style={{ marginTop: "20px" }}>
        <button onClick={() => handleMark("lunch")}>
          {meals.lunch ? "✅ Lunch Marked" : "Mark Lunch"}
        </button>
      </div>

      <div style={{ marginTop: "20px" }}>
        <button onClick={() => handleMark("supper")}>
          {meals.supper ? "✅ Supper Marked" : "Mark Supper"}
        </button>
      </div>
    </div>
  );
}

export default App;
