import React, { useState, useEffect } from "react";
import { auth, db } from "./firebase";
import { doc, getDoc, updateDoc } from "firebase/firestore";
import { getStorage, ref as storageRef, uploadBytes, getDownloadURL } from "firebase/storage";
import { updateProfile } from "firebase/auth";
import "./UserProfilePage.css";

function UserProfilePage({ currentUser, onLogout, onBack }) {
  const [userData, setUserData] = useState({
    name: '',
    department: '',
    phone: '',
    email: ''
  });
  const [loading, setLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [avatarUrl, setAvatarUrl] = useState("");
  // Cropper state
  const [showCropper, setShowCropper] = useState(false);
  const [cropSrc, setCropSrc] = useState("");
  const [imgMeta, setImgMeta] = useState({ w: 0, h: 0 });
  const [zoom, setZoom] = useState(1);
  const [offsetX, setOffsetX] = useState(0);
  const [offsetY, setOffsetY] = useState(0);
  const cropSize = 280; // square preview in px

  // Load user data
  useEffect(() => {
    const loadUserData = async () => {
      if (!currentUser) return;
      
      try {
        const userRef = doc(db, "users", currentUser.uid);
        const userSnap = await getDoc(userRef);
        
        if (userSnap.exists()) {
          const data = userSnap.data();
          setUserData({
            name: data.name || '',
            department: data.department || data.branch || '',
            phone: data.phone || '',
            email: data.email || currentUser.email || ''
          });
          setAvatarUrl(data.photoURL || currentUser?.photoURL || "");
        }
      } catch (error) {
        console.error("Error loading user data:", error);
      } finally {
        setLoading(false);
      }
    };

    loadUserData();
  }, [currentUser]);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!currentUser) return;

    try {
      const userRef = doc(db, "users", currentUser.uid);
      await updateDoc(userRef, {
        name: userData.name,
        department: userData.department,
        phone: userData.phone
      });
      
      setIsEditing(false);
      alert('Profile updated successfully!');
    } catch (error) {
      console.error("Error updating profile:", error);
      alert('Error updating profile. Please try again.');
    }
  };

  // Handle logout
  const handleLogout = async () => {
    try {
      await onLogout();
    } catch (error) {
      console.error("Error logging out:", error);
      alert('Error logging out. Please try again.');
    }
  };

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // File pick -> open cropper
  const onPickAvatar = (e) => {
    const file = e.target.files?.[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = () => {
      const src = reader.result;
      const img = new Image();
      img.onload = () => {
        const w = img.naturalWidth || img.width;
        const h = img.naturalHeight || img.height;
        setImgMeta({ w, h });
        setCropSrc(src);
        // default zoom to cover the square fully
        const initialZoom = Math.max(cropSize / w, cropSize / h);
        setZoom(initialZoom);
        setOffsetX(0);
        setOffsetY(0);
        setShowCropper(true);
      };
      img.src = src;
    };
    reader.readAsDataURL(file);
    // allow selecting same file again
    e.target.value = "";
  };

  // Compute and upload cropped blob
  const confirmCropAndUpload = async () => {
    if (!auth.currentUser || !cropSrc) return;
    try {
      // draw to canvas
      const canvas = document.createElement("canvas");
      canvas.width = cropSize;
      canvas.height = cropSize;
      const ctx = canvas.getContext("2d");

      // Based on preview math (draw directly, let canvas clip)
      const imgW = imgMeta.w;
      const imgH = imgMeta.h;
      const dispW = imgW * zoom;
      const dispH = imgH * zoom;
      const left = (cropSize - dispW) / 2 + offsetX;
      const top = (cropSize - dispH) / 2 + offsetY;

      // Clear and draw (no clamping)
      ctx.clearRect(0, 0, cropSize, cropSize);
      const img = await loadImage(cropSrc);
      ctx.drawImage(img, left, top, dispW, dispH);

      const blob = await new Promise((resolve) => canvas.toBlob(resolve, "image/jpeg", 0.9));

      // Upload
      const storage = getStorage();
      const path = `userAvatars/${auth.currentUser.uid}`;
      const ref = storageRef(storage, path);
      await uploadBytes(ref, blob);
      const url = await getDownloadURL(ref);
      await updateDoc(doc(db, "users", auth.currentUser.uid), { photoURL: url });
      try { await updateProfile(auth.currentUser, { photoURL: url }); } catch (_) {}
      setAvatarUrl(url);
      setShowCropper(false);
      alert("‚úÖ Profile picture updated");
    } catch (err) {
      console.error("Crop/upload failed", err);
      alert("‚ùå Failed to update profile picture");
    }
  };

  function loadImage(src) {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.onload = () => resolve(img);
      img.onerror = reject;
      img.src = src;
    });
  }

  if (loading) {
    return (
      <div className="profile-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading profile...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="profile-content">
        {/* Back to Dashboard (prominent) */}
        {onBack && (
          <div className="back-bar">
            <button className="back-to-home-btn" onClick={onBack}>
              ‚Üê Back to Dashboard
            </button>
          </div>
        )}
        {/* Header */}
        <div className="profile-header" style={{display:'flex', alignItems:'center', justifyContent:'center', gap:12}}>
          <h1 className="profile-title">User Profile</h1>
        </div>

        {/* Avatar Section */}
        <div className="avatar-section">
          <div className="avatar-circle">
            {avatarUrl ? (
              <img src={avatarUrl} alt="Profile" className="avatar-img" />
            ) : (
              (userData.name ? userData.name.charAt(0).toUpperCase() : 'U')
            )}
            <label className="avatar-upload" title="Update profile picture">
              üì∑
              <input type="file" accept="image/*" onChange={onPickAvatar} hidden />
            </label>
          </div>
        </div>

        {showCropper && (
          <div className="modal-overlay">
            <div className="modal-content">
              <div className="modal-header">
                <h2>Crop Profile Picture</h2>
                <button className="close-btn" onClick={() => setShowCropper(false)}>√ó</button>
              </div>
              <div className="modal-body">
                <div className="cropper-wrapper" style={{ width: cropSize, height: cropSize }}>
                  <div className="crop-viewport" style={{ width: cropSize, height: cropSize }}>
                    {cropSrc && (
                      <img
                        src={cropSrc}
                        alt="to-crop"
                        className="crop-image"
                        style={{
                          width: imgMeta.w * zoom,
                          height: imgMeta.h * zoom,
                          transform: `translate(${(cropSize - imgMeta.w * zoom) / 2 + offsetX}px, ${(cropSize - imgMeta.h * zoom) / 2 + offsetY}px)`
                        }}
                      />
                    )}
                  </div>
                </div>
                <div className="crop-controls">
                  <div className="control">
                    <label>Zoom</label>
                    <input type="range" min="0.5" max="3" step="0.01" value={zoom} onChange={(e)=>setZoom(parseFloat(e.target.value))} />
                  </div>
                  <div className="control">
                    <label>Move X</label>
                    <input type="range" min={-cropSize*2} max={cropSize*2} step="1" value={offsetX} onChange={(e)=>setOffsetX(parseInt(e.target.value))} />
                  </div>
                  <div className="control">
                    <label>Move Y</label>
                    <input type="range" min={-cropSize*2} max={cropSize*2} step="1" value={offsetY} onChange={(e)=>setOffsetY(parseInt(e.target.value))} />
                  </div>
                </div>
                <div className="edit-actions" style={{marginTop: 12}}>
                  <button type="button" className="cancel-btn" onClick={()=>setShowCropper(false)}>Cancel</button>
                  <button type="button" className="save-btn" onClick={confirmCropAndUpload}>Save</button>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Profile Form */}
        <form className="profile-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name" className="form-label">Name</label>
            <input
              type="text"
              id="name"
              name="name"
              value={userData.name}
              onChange={handleInputChange}
              className="form-input"
              placeholder="Enter your full name"
              disabled={!isEditing}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="department" className="form-label">Department</label>
            <input
              type="text"
              id="department"
              name="department"
              value={userData.department}
              onChange={handleInputChange}
              className="form-input"
              placeholder="Enter your department"
              disabled={!isEditing}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="phone" className="form-label">Phone</label>
            <input
              type="tel"
              id="phone"
              name="phone"
              value={userData.phone}
              onChange={handleInputChange}
              className="form-input"
              placeholder="Enter your phone number"
              disabled={!isEditing}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="email" className="form-label">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={userData.email}
              className="form-input"
              placeholder="Enter your email"
              disabled
            />
          </div>

          {/* Action Buttons */}
          <div className="form-actions">
            {!isEditing ? (
              <button
                type="button"
                className="edit-btn"
                onClick={() => setIsEditing(true)}
              >
                Edit Profile
              </button>
            ) : (
              <div className="edit-actions">
                <button
                  type="button"
                  className="cancel-btn"
                  onClick={() => setIsEditing(false)}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="save-btn"
                >
                  Save Changes
                </button>
              </div>
            )}
          </div>
        </form>

        {/* Logout Button */}
        <div className="logout-section">
          <button
            className="logout-btn"
            onClick={handleLogout}
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  );
}

export default UserProfilePage;
