import { useState } from "react";
import { auth, sendPasswordResetEmail } from "./firebase";
import "./ResetPage.css";

function ResetPage({ goToLogin, goToSignup }) {
  const [email, setEmail] = useState("");

  // Handle Reset
  const handleReset = async (e) => {
    e.preventDefault();
    try {
      await sendPasswordResetEmail(auth, email);
      alert("üì© Password reset link sent! Check your email.");
    } catch (err) {
      alert("‚ö†Ô∏è " + err.message);
    }
  };

  return (
    <div className="reset-container">
      <form className="reset-box" onSubmit={handleReset}>
            <img src="/logo3.png" alt="Logo" className="login-logo" />
        <h2 className="reset-title1">MEA MESS</h2>
        <h3 className="reset-title2">Reset Password</h3>

        <input
          type="email"
          className="input-field"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />

        <button type="submit" className="btn reset-btn">Send Email</button>

        <p className="link">
          Don‚Äôt have an account?{" "}
          <span onClick={goToSignup}>Signup</span>
        </p>
        <p className="link" onClick={goToLogin}>Login</p>
      </form>
    </div>
  );
}

export default ResetPage;
