import { useState, useEffect } from "react";
import { auth, db, signOut, sendPasswordResetEmail } from "./firebase";
import { onAuthStateChanged } from "firebase/auth";
import { doc, getDoc } from "firebase/firestore";

import LoginPage from "./LoginPage";
import SignupPage from "./SignupPage";
import AdminPage from "./AdminPage";
import StudentPage from "./StudentPage";
import PointSystemPage from "./PointSystemPage";

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showSignup, setShowSignup] = useState(false);

  const [adminView, setAdminView] = useState("dashboard");

  // üîπ Listen for login state
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      if (user) {
        const docRef = doc(db, "users", user.uid);
        const docSnap = await getDoc(docRef);

        if (docSnap.exists()) {
          const userData = docSnap.data();

          // üëá check approval status
          if (userData.status === "pending") {
            alert("‚è≥ Your account is pending admin approval.");
            await signOut(auth);
            setCurrentUser(null);
            setUserRole(null);
            setLoading(false);
            return;
          }

          if (userData.status === "rejected") {
            alert("‚ùå Your account request was rejected by admin.");
            await signOut(auth);
            setCurrentUser(null);
            setUserRole(null);
            setLoading(false);
            return;
          }

          setCurrentUser(user);
          setUserRole(userData.role || "student");
        } else {
          setCurrentUser(null);
          setUserRole(null);
        }
      } else {
        setCurrentUser(null);
        setUserRole(null);
        setAdminView("dashboard");
      }
      setLoading(false);
    });
    return () => unsubscribe();
  }, []);

  // üîπ Handle Logout
  const handleLogout = async () => {
    await signOut(auth);
    setAdminView("dashboard");
  };

  // üîπ Forgot Password
  const handleForgotPassword = async (email) => {
    if (!email) {
      alert("Please enter your email in the login form first!");
      return;
    }
    try {
      await sendPasswordResetEmail(auth, email);
      alert("üì© Password reset email sent!");
    } catch (err) {
      alert("‚ö†Ô∏è " + err.message);
    }
  };

  // üîπ Loading screen
  if (loading) {
    return <h2>Loading...</h2>;
  }

  // üîπ If not logged in ‚Üí Show Login / Signup
  if (!currentUser) {
    return showSignup ? (
      <SignupPage
        onSignupSuccess={() => setShowSignup(false)}
        goToLogin={() => setShowSignup(false)}
      />
    ) : (
      <LoginPage
        onLoginSuccess={() => {}}
        goToSignup={() => setShowSignup(true)}
        onForgotPassword={handleForgotPassword}
      />
    );
  }

  // üîπ If logged in as Admin
  if (userRole === "admin") {
    if (adminView === "points") {
      return <PointSystemPage goBack={() => setAdminView("dashboard")} />;
    }
    return (
      <AdminPage
        onLogout={handleLogout}
        goToPointSystem={() => setAdminView("points")}
      />
    );
  }

  // üîπ If logged in as Student
  return <StudentPage currentUser={currentUser} onLogout={handleLogout} />;
}

export default App;
