import { useState, useEffect } from "react";
import { auth, db } from "./firebase";
import {
  signInWithEmailAndPassword,
  signOut,
  onAuthStateChanged,
} from "firebase/auth";
import {
  doc,
  getDoc,
  setDoc,
  updateDoc,
  increment,
  onSnapshot,
} from "firebase/firestore";
import SignupPage from "./SignupPage";
import AdminPage from "./AdminPage";

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [loading, setLoading] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showSignup, setShowSignup] = useState(false);
  const [mealMarked, setMealMarked] = useState({
    breakfast: false,
    lunch: false,
    supper: false,
  });

  // ðŸ”¹ Listen for login state
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      if (user) {
        setCurrentUser(user);

        // get role from Firestore
        const docRef = doc(db, "users", user.uid);
        const docSnap = await getDoc(docRef);

        if (docSnap.exists()) {
          setUserRole(docSnap.data().role || "student");
        } else {
          setUserRole("student");
        }

        // fetch meals marked by user
        const userMealsRef = doc(db, "userMeals", user.uid);
        const userMealsSnap = await getDoc(userMealsRef);
        if (userMealsSnap.exists()) {
          setMealMarked(userMealsSnap.data());
        }
      } else {
        setCurrentUser(null);
        setUserRole(null);
        setMealMarked({ breakfast: false, lunch: false, supper: false });
      }
      setLoading(false);
    });
    return () => unsubscribe();
  }, []);

  // ðŸ”¹ Handle Login
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, password);
    } catch (err) {
      alert(err.message);
    }
  };

  // ðŸ”¹ Handle Logout
  const handleLogout = async () => {
    await signOut(auth);
  };

  // ðŸ”¹ Toggle a meal
  const toggleMeal = async (meal) => {
    try {
      const mealRef = doc(db, "meals", meal);
      const userMealsRef = doc(db, "userMeals", currentUser.uid);

      if (mealMarked[meal]) {
        // unmark meal
        await updateDoc(mealRef, { count: increment(-1) });
        await setDoc(
          userMealsRef,
          { ...mealMarked, [meal]: false },
          { merge: true }
        );
        setMealMarked((prev) => ({ ...prev, [meal]: false }));
      } else {
        // mark meal
        await setDoc(mealRef, { count: increment(1) }, { merge: true });
        await setDoc(
          userMealsRef,
          { ...mealMarked, [meal]: true },
          { merge: true }
        );
        setMealMarked((prev) => ({ ...prev, [meal]: true }));
      }
    } catch (err) {
      console.error(err);
    }
  };

  // ðŸ”¹ Loading screen
  if (loading) {
    return <h2>Loading...</h2>;
  }

  // ðŸ”¹ If not logged in â†’ Show Login / Signup
  if (!currentUser) {
    return (
      <div>
        {showSignup ? (
          <SignupPage onSignupSuccess={() => setShowSignup(false)} />
        ) : (
          <div style={{ textAlign: "center", marginTop: "50px" }}>
            <h2>Login</h2>
            <form onSubmit={handleLogin}>
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <br />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <br />
              <button type="submit">Login</button>
            </form>
            <p>
              Donâ€™t have an account?{" "}
              <button onClick={() => setShowSignup(true)}>Sign Up</button>
            </p>
          </div>
        )}
      </div>
    );
  }

  // ðŸ”¹ If logged in as Admin â†’ Show AdminPage
  if (userRole === "admin") {
    return (
      <div>
        <AdminPage />
        <button onClick={handleLogout}>Logout</button>
      </div>
    );
  }

  // ðŸ”¹ If logged in as Student â†’ Show Meal Toggling
  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>Welcome, {currentUser.email}</h2>
      <h3>Mark / Unmark your meals</h3>
      <button onClick={() => toggleMeal("breakfast")}>
        {mealMarked.breakfast ? "Unmark Breakfast" : "Mark Breakfast"}
      </button>
      <button onClick={() => toggleMeal("lunch")}>
        {mealMarked.lunch ? "Unmark Lunch" : "Mark Lunch"}
      </button>
      <button onClick={() => toggleMeal("supper")}>
        {mealMarked.supper ? "Unmark Supper" : "Mark Supper"}
      </button>
      <br />
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
}

export default App;
