import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { collection, getDocs } from "firebase/firestore";
import "./AdminPage.css";

function AdminPage({ onLogout }) {
  const [mealCounts, setMealCounts] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMealCounts = async () => {
      try {
        const usersRef = collection(db, "users");
        const querySnapshot = await getDocs(usersRef);

        const counts = {};
        querySnapshot.forEach((docSnap) => {
          const data = docSnap.data();
          if (data.meals) {
            Object.entries(data.meals).forEach(([day, meals]) => {
              Object.entries(meals).forEach(([meal, status]) => {
                if (["breakfast", "lunch", "supper"].includes(meal)) {
                  if (!counts[day]) counts[day] = { breakfast: 0, lunch: 0, supper: 0 };
                  if (status === true) counts[day][meal] += 1;
                }
              });
            });
          }
        });

        setMealCounts(counts);
      } catch (err) {
        console.error("Error fetching admin data:", err);
      }
      setLoading(false);
    };

    fetchMealCounts();
  }, []);

  if (loading) return <h2 className="loading">Loading counts...</h2>;

  return (
    <div className="admin-container">
      <h2 className="title">üìä Admin Dashboard</h2>
      {Object.entries(mealCounts).map(([day, meals]) => (
        <div key={day} className="day-row">
          <h3 className="day-label">{day}</h3>
          <p>üç≥ Breakfast: {meals.breakfast}</p>
          <p>ü•ó Lunch: {meals.lunch}</p>
          <p>üçõ Supper: {meals.supper}</p>
        </div>
      ))}

      {/* logout bottom-right */}
      <div className="logout-wrapper">
        <button className="logout-btn" onClick={onLogout}>
          Logout
        </button>
      </div>
    </div>
  );
}

export default AdminPage;
