// AdminPage.js
import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { collection, getDocs } from "firebase/firestore";
import "./AdminPage.css";

function AdminPage() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  // Point system
  const calculatePoints = (meals) => {
    let totalPoints = 0;
    const today = new Date().toISOString().split("T")[0];

    Object.keys(meals || {}).forEach((day) => {
      if (day > today) return; // ‚ùå Skip future days

      const { breakfast, lunch, supper } = meals[day];
      const markedCount = [breakfast, lunch, supper].filter(Boolean).length;

      if (markedCount === 3) totalPoints += 1;
      else if (markedCount === 2) totalPoints += 0.68;
      else if (markedCount === 1) totalPoints += 0.35;
    });

    return totalPoints;
  };

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "users"));
        const userList = querySnapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));
        setUsers(userList);
      } catch (err) {
        console.error("Error fetching users:", err);
      }
      setLoading(false);
    };
    fetchUsers();
  }, []);

  if (loading) return <h2 className="loading">Loading admin data...</h2>;

  return (
    <div className="admin-container">
      <h2 className="admin-title">üèÜ Monthly Points</h2>
      <table className="admin-table">
        <thead>
          <tr>
            <th>Student</th>
            <th>Total Points</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.email}</td>
              <td>{calculatePoints(user.meals).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminPage;
