import { useState } from "react";
import { auth, db, createUserWithEmailAndPassword } from "./firebase";
import { doc, setDoc } from "firebase/firestore";
import "./signuppage.css";

function SignupPage({ onSignupSuccess, goToLogin }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // üîπ Handle Signup
  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);

      // Save user in Firestore as student
      await setDoc(doc(db, "users", userCredential.user.uid), {
        email,
        role: "student",
      });

      alert("‚úÖ Signup successful!");
      onSignupSuccess();
    } catch (err) {
      alert("‚ö†Ô∏è " + err.message);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2 className="auth-title">Sign Up</h2>
        <form onSubmit={handleSignup} className="auth-form">
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Create a password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" className="auth-btn">Sign Up</button>
        </form>

        <p className="auth-text">
          Already have an account?{" "}
          <button className="link-btn" onClick={goToLogin}>Login</button>
        </p>
      </div>
    </div>
  );
}

export default SignupPage;
