import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { collection, onSnapshot } from "firebase/firestore";

const AdminPage = () => {
  const [counts, setCounts] = useState({ breakfast: 0, lunch: 0, supper: 0 });

  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, "users"), (snapshot) => {
      let breakfast = 0, lunch = 0, supper = 0;

      snapshot.forEach((doc) => {
        const meals = doc.data().meals || {};
        if (meals.breakfast) breakfast++;
        if (meals.lunch) lunch++;
        if (meals.supper) supper++;
      });

      setCounts({ breakfast, lunch, supper });
    });

    return () => unsubscribe();
  }, []);

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>Admin Dashboard</h2>
      <p>üç≥ Breakfast Count: {counts.breakfast}</p>
      <p>üçõ Lunch Count: {counts.lunch}</p>
      <p>ü•ò Supper Count: {counts.supper}</p>
    </div>
  );
};

export default AdminPage;
