// App.js
import { useState, useEffect } from "react";
import { auth, db, signOut, sendPasswordResetEmail } from "./firebase";
import { onAuthStateChanged } from "firebase/auth";
import { doc, getDoc } from "firebase/firestore";

import LoginPage from "./LoginPage";
import SignupPage from "./SignupPage";
import AdminPage from "./AdminPage";
import StudentPage from "./StudentPage";

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showSignup, setShowSignup] = useState(false);

  // üîπ Listen for login state
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      if (user) {
        setCurrentUser(user);

        try {
          // get role from Firestore
          const docRef = doc(db, "users", user.uid);
          const docSnap = await getDoc(docRef);

          if (docSnap.exists()) {
            setUserRole(docSnap.data().role || "student");
          } else {
            setUserRole("student");
          }
        } catch (err) {
          console.error("‚ùå Firestore error:", err.message);
          alert("‚ö†Ô∏è Firestore connection issue: " + err.message);
        }
      } else {
        setCurrentUser(null);
        setUserRole(null);
      }
      setLoading(false);
    });
    return () => unsubscribe();
  }, []);

  // üîπ Handle Logout
  const handleLogout = async () => {
    try {
      await signOut(auth);
    } catch (err) {
      console.error("Logout error:", err.message);
    }
  };

  // üîπ Forgot Password
  const handleForgotPassword = async (email) => {
    if (!email) {
      alert("Please enter your email in the login form first!");
      return;
    }
    try {
      await sendPasswordResetEmail(auth, email);
      alert("üì© Password reset email sent!");
    } catch (err) {
      alert("‚ö†Ô∏è " + err.message);
    }
  };

  // üîπ Loading screen
  if (loading) {
    return <h2 style={{ textAlign: "center" }}>Loading...</h2>;
  }

  // üîπ If not logged in ‚Üí Show Login / Signup
  if (!currentUser) {
    return showSignup ? (
      <SignupPage
        onSignupSuccess={() => setShowSignup(false)}
        goToLogin={() => setShowSignup(false)}
      />
    ) : (
      <LoginPage
        onLoginSuccess={() => {}}
        goToSignup={() => setShowSignup(true)}
        onForgotPassword={handleForgotPassword}
      />
    );
  }

  // üîπ If logged in as Admin ‚Üí Show AdminPage
  if (userRole === "admin") {
    return <AdminPage onLogout={handleLogout} />;
  }

  // üîπ If logged in as Student ‚Üí Show StudentPage
  return <StudentPage currentUser={currentUser} onLogout={handleLogout} />;
}

export default App;
