import React, { useEffect, useState } from "react";
import { auth, db, provider } from "./firebase";
import { signInWithPopup, signOut } from "firebase/auth";
import {
  doc,
  getDoc,
  setDoc,
  updateDoc,
  collection,
  getDocs,
} from "firebase/firestore";

function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);
  const [meals, setMeals] = useState({ breakfast: false, lunch: false, supper: false });
  const [mealCounts, setMealCounts] = useState({ breakfast: 0, lunch: 0, supper: 0 });

  // Google login
  const handleLogin = async () => {
    const result = await signInWithPopup(auth, provider);
    const loggedUser = result.user;
    setUser(loggedUser);

    const userRef = doc(db, "users", loggedUser.uid);
    const docSnap = await getDoc(userRef);

    if (!docSnap.exists()) {
      // New user â†’ default role student
      await setDoc(userRef, {
        name: loggedUser.displayName,
        email: loggedUser.email,
        role: "student",
        meals: { breakfast: false, lunch: false, supper: false },
      });
      setRole("student");
    } else {
      setRole(docSnap.data().role);
      setMeals(docSnap.data().meals || meals);
    }
  };

  // Logout
  const handleLogout = async () => {
    await signOut(auth);
    setUser(null);
    setRole(null);
    setMeals({ breakfast: false, lunch: false, supper: false });
  };

  // Mark meal (students)
  const handleMealMark = async (mealName) => {
    if (meals[mealName]) {
      alert(`You already marked ${mealName}!`);
      return;
    }
    const userRef = doc(db, "users", user.uid);
    const newMeals = { ...meals, [mealName]: true };
    await updateDoc(userRef, { meals: newMeals });
    setMeals(newMeals);
  };

  // Fetch meal counts (admins)
  const fetchMealCounts = async () => {
    const usersSnapshot = await getDocs(collection(db, "users"));
    let counts = { breakfast: 0, lunch: 0, supper: 0 };

    usersSnapshot.forEach((doc) => {
      const data = doc.data();
      if (data.meals) {
        if (data.meals.breakfast) counts.breakfast++;
        if (data.meals.lunch) counts.lunch++;
        if (data.meals.supper) counts.supper++;
      }
    });

    setMealCounts(counts);
  };

  useEffect(() => {
    if (role === "admin") {
      fetchMealCounts();
    }
  }, [role]);

  if (!user) {
    return (
      <div className="p-6 text-center">
        <h1 className="text-2xl font-bold">Mess App</h1>
        <button
          onClick={handleLogin}
          className="mt-4 px-4 py-2 bg-blue-600 text-white rounded"
        >
          Login with Google
        </button>
      </div>
    );
  }

  return (
    <div className="p-6 text-center">
      <h1 className="text-2xl font-bold">Welcome, {user.displayName}</h1>
      <p>Role: {role}</p>
      <button
        onClick={handleLogout}
        className="mt-2 px-3 py-1 bg-red-500 text-white rounded"
      >
        Logout
      </button>

      {/* Student View */}
      {role === "student" && (
        <div className="mt-6">
          <h2 className="text-xl mb-2">Mark your meals</h2>
          {["breakfast", "lunch", "supper"].map((meal) => (
            <button
              key={meal}
              onClick={() => handleMealMark(meal)}
              disabled={meals[meal]}
              className={`block mx-auto my-2 px-4 py-2 rounded ${
                meals[meal] ? "bg-gray-400" : "bg-green-600 text-white"
              }`}
            >
              {meals[meal] ? `Already marked ${meal}` : `Mark ${meal}`}
            </button>
          ))}
        </div>
      )}

      {/* Admin View */}
      {role === "admin" && (
        <div className="mt-6">
          <h2 className="text-xl mb-2">Meal Counts (Admin)</h2>
          <p>Breakfast: {mealCounts.breakfast}</p>
          <p>Lunch: {mealCounts.lunch}</p>
          <p>Supper: {mealCounts.supper}</p>
          <button
            onClick={fetchMealCounts}
            className="mt-4 px-3 py-1 bg-blue-500 text-white rounded"
          >
            Refresh Counts
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
