import { useState, useEffect } from "react";
import { auth, db } from "./firebase";
import {
  signInWithEmailAndPassword,
  signOut,
  onAuthStateChanged,
} from "firebase/auth";
import { doc, getDoc } from "firebase/firestore";
import SignupPage from "./SignupPage";
import AdminPage from "./AdminPage";
import StudentPage from "./StudentPage";

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [loading, setLoading] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showSignup, setShowSignup] = useState(false);

  // ðŸ”¹ Listen for login state
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      if (user) {
        setCurrentUser(user);

        // get role from Firestore
        const docRef = doc(db, "users", user.uid);
        const docSnap = await getDoc(docRef);

        if (docSnap.exists()) {
          setUserRole(docSnap.data().role || "student");
        } else {
          setUserRole("student");
        }
      } else {
        setCurrentUser(null);
        setUserRole(null);
      }
      setLoading(false);
    });
    return () => unsubscribe();
  }, []);

  // ðŸ”¹ Handle Login
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, password);
    } catch (err) {
      alert(err.message);
    }
  };

  // ðŸ”¹ Handle Logout
  const handleLogout = async () => {
    await signOut(auth);
  };

  // ðŸ”¹ Loading screen
  if (loading) {
    return <h2>Loading...</h2>;
  }

  // ðŸ”¹ If not logged in â†’ Show Login / Signup
  if (!currentUser) {
    return (
      <div style={{ textAlign: "center", marginTop: "50px" }}>
        {showSignup ? (
          <SignupPage onSignupSuccess={() => setShowSignup(false)} />
        ) : (
          <div>
            <h2>Login</h2>
            <form onSubmit={handleLogin}>
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <br />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <br />
              <button type="submit">Login</button>
            </form>
            <p>
              Donâ€™t have an account?{" "}
              <button onClick={() => setShowSignup(true)}>Sign Up</button>
            </p>
          </div>
        )}
      </div>
    );
  }

  // ðŸ”¹ If logged in as Admin â†’ Show AdminPage
  if (userRole === "admin") {
    return (
      <AdminPage handleLogout={handleLogout} />
    );
  }

  // ðŸ”¹ If logged in as Student â†’ Show StudentPage
  return <StudentPage handleLogout={handleLogout} />;
}

export default App;
