import React, { useEffect, useState } from "react";
import { db, auth } from "./firebase";
import { doc, getDoc, updateDoc, setDoc } from "firebase/firestore";

const StudentPage = () => {
  const [meals, setMeals] = useState({ breakfast: false, lunch: false, supper: false });

  useEffect(() => {
    const fetchMeals = async () => {
      const userRef = doc(db, "users", auth.currentUser.uid);
      const userSnap = await getDoc(userRef);
      if (userSnap.exists()) {
        setMeals(userSnap.data().meals || { breakfast: false, lunch: false, supper: false });
      } else {
        // first time user → create doc
        await setDoc(userRef, {
          role: "student",
          meals: { breakfast: false, lunch: false, supper: false },
        });
      }
    };
    fetchMeals();
  }, []);

  const toggleMeal = async (meal) => {
    const newMeals = { ...meals, [meal]: !meals[meal] };
    setMeals(newMeals);
    const userRef = doc(db, "users", auth.currentUser.uid);
    await updateDoc(userRef, { meals: newMeals });
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>Mark Your Meals</h2>
      <button onClick={() => toggleMeal("breakfast")}>
        {meals.breakfast ? "✅ Breakfast Marked" : "☑️ Mark Breakfast"}
      </button>
      <br /><br />
      <button onClick={() => toggleMeal("lunch")}>
        {meals.lunch ? "✅ Lunch Marked" : "☑️ Mark Lunch"}
      </button>
      <br /><br />
      <button onClick={() => toggleMeal("supper")}>
        {meals.supper ? "✅ Supper Marked" : "☑️ Mark Supper"}
      </button>
    </div>
  );
};

export default StudentPage;
