// AdminPage.js
import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import {
  collection,
  getDocs,
  doc,
  getDoc,
  setDoc,
  updateDoc,
  query,
  where,
} from "firebase/firestore";
import "./AdminPage.css";

function AdminPage({ onLogout, goToPointSystem }) {
  const [mealCounts, setMealCounts] = useState({});
  const [userPoints, setUserPoints] = useState({});
  const [loading, setLoading] = useState(true);

  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [generated, setGenerated] = useState(false);

  const [allUsers, setAllUsers] = useState([]);
  const [view, setView] = useState("dashboard"); // toggle between dashboard & requests

  // default rules
  const defaultSystem = {
    X: 1,
    "0": 0,
    B: 0.25,
    L: 0.35,
    S: 0.45,
    "B/L": 0.65,
    "B/S": 0.75,
    "L/S": 0.85,
  };

  const [pointSystem, setPointSystem] = useState(defaultSystem);

  // --- load settings + users + meal counts --------------------
  useEffect(() => {
    const fetchAll = async () => {
      try {
        // load point system
        const settingsRef = doc(db, "settings", "points");
        const settingsSnap = await getDoc(settingsRef);
        if (settingsSnap.exists()) {
          setPointSystem(settingsSnap.data());
        } else {
          await setDoc(settingsRef, defaultSystem);
        }

        // load users
        const usersRef = collection(db, "users");
        const querySnapshot = await getDocs(usersRef);

        const all = [];
        const counts = {};
        const today = new Date();

        // next 8 days (today + 7)
        const nextDays = [];
        for (let i = 0; i < 8; i++) {
          const d = new Date(today);
          d.setDate(today.getDate() + i);
          const dateStr = d.toISOString().split("T")[0];
          nextDays.push(dateStr);
          counts[dateStr] = { breakfast: 0, lunch: 0, supper: 0 };
        }

        querySnapshot.forEach((docSnap) => {
          const data = docSnap.data();
          all.push(data);

          const userMeals = data.meals || {};
          nextDays.forEach((day) => {
            const meals = userMeals[day];
            if (meals) {
              ["breakfast", "lunch", "supper"].forEach((meal) => {
                if (meals[meal]) counts[day][meal] += 1;
              });
            } else {
              const todayStr = new Date().toISOString().split("T")[0];
              if (day > todayStr) {
                counts[day].breakfast += 1;
                counts[day].lunch += 1;
                counts[day].supper += 1;
              }
            }
          });
        });

        setAllUsers(all);
        setMealCounts(counts);
      } catch (err) {
        console.error("Admin data error:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchAll();
  }, []);

  // --- approve/reject users --------------------
  const updateUserStatus = async (email, newStatus) => {
    try {
      const usersRef = collection(db, "users");
      const q = query(usersRef, where("email", "==", email));
      const snapshot = await getDocs(q);

      snapshot.forEach(async (docSnap) => {
        await updateDoc(doc(db, "users", docSnap.id), { status: newStatus });
      });

      alert(`‚úÖ User ${email} marked as ${newStatus}`);
      setAllUsers((prev) =>
        prev.map((u) => (u.email === email ? { ...u, status: newStatus } : u))
      );
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  // --- generate report --------------------
  const generateReport = () => {
    if (!fromDate || !toDate) {
      alert("‚ö†Ô∏è Please select both From and To dates!");
      return;
    }

    const points = {};
    const todayStr = new Date().toISOString().split("T")[0];

    allUsers.forEach((user) => {
      if (user.status !== "approved") return; // only approved users count

      const email = user.email || "Unknown";
      const userMeals = user.meals || {};
      let totalPoints = 0;

      const mealDates = Object.keys(userMeals).sort();
      const firstMealDate = mealDates.length > 0 ? mealDates[0] : todayStr;

      const start = new Date(fromDate);
      const end = new Date(toDate);
      start.setHours(0, 0, 0, 0);
      end.setHours(0, 0, 0, 0);

      for (let d = new Date(start.getTime()); d <= end; d.setDate(d.getDate() + 1)) {
        const day = d.toISOString().split("T")[0];

        let meals = userMeals[day];
        if (!meals) {
          if (day > todayStr) {
            meals = { breakfast: true, lunch: true, supper: true };
          } else if (day >= firstMealDate) {
            meals = { breakfast: true, lunch: true, supper: true };
          } else {
            continue;
          }
        }

        const marked = { B: !!meals.breakfast, L: !!meals.lunch, S: !!meals.supper };
        const markedArr = ["B", "L", "S"].filter((m) => marked[m]);

        let key;
        if (markedArr.length === 3) key = "X";
        else if (markedArr.length === 0) key = "0";
        else key = markedArr.join("/");

        totalPoints += pointSystem[key] ?? 0;
      }

      points[email] = totalPoints.toFixed(2);
    });

    setUserPoints(points);
    setGenerated(true);
  };

  // --- export CSV --------------------
  const exportCSV = () => {
    const rows = [["Email", "Points"]];
    Object.entries(userPoints).forEach(([email, pts]) => {
      rows.push([email, pts]);
    });

    const csvContent = rows.map((r) => r.join(",")).join("\n");
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });

    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.setAttribute("download", `points_${fromDate}_to_${toDate}.csv`);
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  if (loading) return <h2 className="loading">Loading data...</h2>;

  // --- Requests View ---
  if (view === "requests") {
    return (
      <div className="admin-container">
        <h2 className="title">üÜï Pending User Requests</h2>
        <table className="meal-table">
          <thead>
            <tr>
              <th>Name</th><th>Email</th><th>Year</th><th>Branch</th><th>Phone</th><th>Hostel</th><th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {allUsers.filter((u) => u.status === "pending").map((user) => (
              <tr key={user.email}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.year}</td>
                <td>{user.branch}</td>
                <td>{user.phone}</td>
                <td>{user.mea}</td>
                <td>
                  <button className="btn approve-btn" onClick={() => updateUserStatus(user.email, "approved")}>‚úÖ Approve</button>
                  <button className="btn reject-btn" onClick={() => updateUserStatus(user.email, "rejected")}>‚ùå Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="admin-actions">
          <button className="btn" onClick={() => setView("dashboard")}>‚¨ÖÔ∏è Back</button>
          <button className="logout-btn" onClick={onLogout}>Logout</button>
        </div>
      </div>
    );
  }

  // --- Dashboard View ---
  return (
    <div className="admin-container">
      <h2 className="title">üìä Admin Dashboard</h2>

      <h3>ü•ò Meal Counts (Next 8 Days)</h3>
      <table className="meal-table">
        <thead>
          <tr>
            <th className="date-col">Date</th>
            <th>üç≥ Breakfast</th>
            <th>ü•ó Lunch</th>
            <th>üçõ Supper</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(mealCounts).map(([day, meals]) => (
            <tr key={day}>
              <td className="date-col">{day}</td>
              <td>{meals.breakfast}</td>
              <td>{meals.lunch}</td>
              <td>{meals.supper}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>Generate Points Report</h3>
      <div className="report-filters">
        <label>
          From:{" "}
          <input type="date" value={fromDate} onChange={(e) => setFromDate(e.target.value)} />
        </label>
        <label>
          To:{" "}
          <input type="date" value={toDate} onChange={(e) => setToDate(e.target.value)} />
        </label>
        <button className="btn generate-btn" onClick={generateReport}>Generate</button>
      </div>

      {generated && (
        <>
          <table className="meal-table">
            <thead>
              <tr>
                <th>Email</th>
                <th>Total Points</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(userPoints).map(([email, points]) => (
                <tr key={email}>
                  <td>{email}</td>
                  <td>{points}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <button className="btn export-btn" onClick={exportCSV}>‚¨áÔ∏è Download CSV</button>
        </>
      )}

      <div className="admin-actions">
        <button className="btn points-btn" onClick={goToPointSystem}>‚öôÔ∏è Point System</button>
        <button className="btn" onClick={() => setView("requests")}>üë• User Requests</button>
        <button className="logout-btn" onClick={onLogout}>Logout</button>
      </div>
    </div>
  );
}

export default AdminPage;
