import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { collection, getDocs, doc, getDoc, setDoc } from "firebase/firestore";
import "./AdminPage.css";

function AdminPage({ onLogout }) {
  const [mealCounts, setMealCounts] = useState({});
  const [userPoints, setUserPoints] = useState({});
  const [loading, setLoading] = useState(true);

  // Point system settings
  const [pointSystem, setPointSystem] = useState({
    oneMeal: 0.35,
    twoMeals: 0.68,
    threeMeals: 1,
  });

  // Load settings + users
  useEffect(() => {
    const fetchData = async () => {
      try {
        // 🔹 Load point system from Firestore
        const settingsRef = doc(db, "settings", "points");
        const settingsSnap = await getDoc(settingsRef);
        if (settingsSnap.exists()) {
          setPointSystem(settingsSnap.data());
        }

        // 🔹 Load users
        const usersRef = collection(db, "users");
        const querySnapshot = await getDocs(usersRef);

        const counts = {};
        const points = {};

        const today = new Date();
        const currentMonth = today.getMonth();
        const currentYear = today.getFullYear();

        // ✅ Next 7 days
        const next7Days = [];
        for (let i = 0; i < 7; i++) {
          const d = new Date(today);
          d.setDate(today.getDate() + i);
          const dateStr = d.toISOString().split("T")[0];
          next7Days.push(dateStr);
          counts[dateStr] = { breakfast: 0, lunch: 0, supper: 0 };
        }

        querySnapshot.forEach((docSnap) => {
          const data = docSnap.data();
          const email = data.email || "Unknown";
          const userMeals = data.meals || {};

          let totalPoints = 0;

          // ✅ Calculate monthly points only up to TODAY
          Object.entries(userMeals).forEach(([day, meals]) => {
            const dateObj = new Date(day);
            const dateStr = day;

            if (
              dateObj.getMonth() === currentMonth &&
              dateObj.getFullYear() === currentYear &&
              dateStr <= today.toISOString().split("T")[0]
            ) {
              const mealCount = ["breakfast", "lunch", "supper"].filter(
                (m) => meals[m]
              ).length;

              if (mealCount === 3) totalPoints += pointSystem.threeMeals;
              else if (mealCount === 2) totalPoints += pointSystem.twoMeals;
              else if (mealCount === 1) totalPoints += pointSystem.oneMeal;
            }
          });

          points[email] = totalPoints.toFixed(2);

          // ✅ Meal counts (next 7 days, only if marked)
          next7Days.forEach((day) => {
            const meals = userMeals[day];
            if (meals) {
              ["breakfast", "lunch", "supper"].forEach((meal) => {
                if (meals[meal]) counts[day][meal] += 1;
              });
            }
          });
        });

        setMealCounts(counts);
        setUserPoints(points);
      } catch (err) {
        console.error("Error fetching admin data:", err);
      }
      setLoading(false);
    };

    fetchData();
  }, [pointSystem]);

  // 🔹 Save updated point system
  const savePointSystem = async () => {
    try {
      await setDoc(doc(db, "settings", "points"), pointSystem);
      alert("✅ Point system updated!");
    } catch (err) {
      alert("⚠️ Failed to save point system: " + err.message);
    }
  };

  if (loading) return <h2 className="loading">Loading data...</h2>;

  return (
    <div className="admin-container">
      <h2 className="title">📊 Admin Dashboard</h2>

      {/* Point system settings */}
      <div className="settings-box">
        <h3>⚙️ Point System</h3>
        <label>
          1 Meal:{" "}
          <input
            type="number"
            step="0.01"
            value={pointSystem.oneMeal}
            onChange={(e) =>
              setPointSystem({ ...pointSystem, oneMeal: parseFloat(e.target.value) })
            }
          />
        </label>
        <label>
          2 Meals:{" "}
          <input
            type="number"
            step="0.01"
            value={pointSystem.twoMeals}
            onChange={(e) =>
              setPointSystem({ ...pointSystem, twoMeals: parseFloat(e.target.value) })
            }
          />
        </label>
        <label>
          3 Meals:{" "}
          <input
            type="number"
            step="0.01"
            value={pointSystem.threeMeals}
            onChange={(e) =>
              setPointSystem({ ...pointSystem, threeMeals: parseFloat(e.target.value) })
            }
          />
        </label>
        <button onClick={savePointSystem} className="save-btn">
          Save
        </button>
      </div>

      {/* Meal counts table */}
      <h2 className="title">🥘 Meal Counts (Next 7 Days)</h2>
      <table className="meal-table">
        <thead>
          <tr>
            <th>Date</th>
            <th>🍳 Breakfast</th>
            <th>🥗 Lunch</th>
            <th>🍛 Supper</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(mealCounts).map(([day, meals]) => (
            <tr key={day}>
              <td>{day}</td>
              <td>{meals.breakfast}</td>
              <td>{meals.lunch}</td>
              <td>{meals.supper}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Points table */}
      <h2 className="title">🏆 Monthly Points</h2>
      <table className="meal-table">
        <thead>
          <tr>
            <th>Student</th>
            <th>Total Points</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(userPoints).map(([email, points]) => (
            <tr key={email}>
              <td>{email}</td>
              <td>{points}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* logout bottom-right */}
      <div className="logout-wrapper">
        <button className="logout-btn" onClick={onLogout}>
          Logout
        </button>
      </div>
    </div>
  );
}

export default AdminPage;
