// App.js
import { useState, useEffect } from "react";
import { auth, db, signOut, sendPasswordResetEmail } from "./firebase";
import { onAuthStateChanged } from "firebase/auth";
import { doc, getDoc } from "firebase/firestore";

import LoginPage from "./LoginPage";
import SignupPage from "./SignupPage";
import AdminPage from "./AdminPage";
import StudentPage from "./StudentPage";
import PointSystemPage from "./PointSystemPage";

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showSignup, setShowSignup] = useState(false);

  // admin subrouting: "dashboard" | "points"
  const [adminView, setAdminView] = useState("dashboard");

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      if (user) {
        setCurrentUser(user);
        try {
          const docRef = doc(db, "users", user.uid);
          const docSnap = await getDoc(docRef);
          setUserRole(docSnap.exists() ? docSnap.data().role || "student" : "student");
        } catch (e) {
          console.error("Firestore error:", e);
          alert("Firestore connection issue: " + e.message);
        }
      } else {
        setCurrentUser(null);
        setUserRole(null);
      }
      setLoading(false);
    });
    return () => unsubscribe();
  }, []);

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setAdminView("dashboard");
    } catch (err) {
      console.error("Logout error:", err.message);
    }
  };

  const handleForgotPassword = async (email) => {
    if (!email) return alert("Please enter your email in the login form first!");
    try {
      await sendPasswordResetEmail(auth, email);
      alert("üì© Password reset email sent!");
    } catch (err) {
      alert("‚ö†Ô∏è " + err.message);
    }
  };

  if (loading) return <h2 style={{ textAlign: "center" }}>Loading...</h2>;

  if (!currentUser) {
    return showSignup ? (
      <SignupPage onSignupSuccess={() => setShowSignup(false)} goToLogin={() => setShowSignup(false)} />
    ) : (
      <LoginPage onLoginSuccess={() => {}} goToSignup={() => setShowSignup(true)} onForgotPassword={handleForgotPassword} />
    );
  }

  if (userRole === "admin") {
    if (adminView === "points") {
      return <PointSystemPage goBack={() => setAdminView("dashboard")} />;
    }
    return <AdminPage onLogout={handleLogout} goToPointSystem={() => setAdminView("points")} />;
  }

  return <StudentPage currentUser={currentUser} onLogout={handleLogout} />;
}

export default App;
