import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { collection, getDocs, doc, getDoc, setDoc } from "firebase/firestore";
import "./AdminPage.css";

function AdminPage({ onLogout, goToPointSystem }) {
  const [mealCounts, setMealCounts] = useState({});
  const [userPoints, setUserPoints] = useState({});
  const [loading, setLoading] = useState(true);

  // default rules (used only if settings/points not present)
  const defaultSystem = {
    X: 1,
    "0": 0,
    B: 0.25,
    L: 0.35,
    S: 0.45,
    "B/L": 0.65,
    "B/S": 0.75,
    "L/S": 0.85,
  };

  const [pointSystem, setPointSystem] = useState(defaultSystem);

  useEffect(() => {
    const fetchAll = async () => {
      setLoading(true);
      try {
        // --- load point system (from settings/points or set default)
        const settingsRef = doc(db, "settings", "points");
        const settingsSnap = await getDoc(settingsRef);
        let loadedPoints = defaultSystem;
        if (settingsSnap.exists()) {
          loadedPoints = settingsSnap.data();
        } else {
          await setDoc(settingsRef, defaultSystem);
        }

        // --- load users
        const usersRef = collection(db, "users");
        const querySnapshot = await getDocs(usersRef);

        const counts = {};
        const points = {};

        const today = new Date();
        const todayStr = today.toISOString().split("T")[0];
        const currentMonth = today.getMonth();
        const currentYear = today.getFullYear();

        // next 8 days (today + 7 more)
        const nextDays = [];
        for (let i = 0; i < 8; i++) {
          const d = new Date(today);
          d.setDate(today.getDate() + i);
          const dateStr = d.toISOString().split("T")[0];
          nextDays.push(dateStr);
          counts[dateStr] = { breakfast: 0, lunch: 0, supper: 0 };
        }

        // iterate users
        querySnapshot.forEach((docSnap) => {
          const data = docSnap.data();
          const email = data.email || "Unknown";
          const userMeals = data.meals || {};

          // --- points calculation (monthly, up to TODAY inclusive)
          let totalPoints = 0;
          Object.entries(userMeals).forEach(([day, meals]) => {
            // ensure day is a date string
            if (!day) return;
            const dateObj = new Date(day);
            const dateStr = day;

            if (
              dateObj.getMonth() === currentMonth &&
              dateObj.getFullYear() === currentYear &&
              dateStr <= todayStr
            ) {
              // Use canonical order B, L, S for keys so we match stored mapping
              const markedMap = {
                B: !!meals.breakfast,
                L: !!meals.lunch,
                S: !!meals.supper,
              };

              const order = ["B", "L", "S"];
              const unmarkedOrdered = order.filter((k) => !markedMap[k]);

              let key;
              if (unmarkedOrdered.length === 3) key = "X";
              else if (unmarkedOrdered.length === 0) key = "0";
              else key = unmarkedOrdered.join("/");

              totalPoints += (loadedPoints[key] ?? 0);
            }
          });

          // round to 2 decimals
          points[email] = Number(totalPoints.toFixed(2));

          // --- counts for the next 8 days
          nextDays.forEach((day) => {
            const meals = userMeals[day]; // may be undefined
            ["breakfast", "lunch", "supper"].forEach((meal) => {
              if (meals === undefined) {
                // If the user has no record for this day:
                // - FUTURE days => default marked (count them)
                // - TODAY/Past => don't count (they didn't mark)
                if (day > todayStr) {
                  counts[day][meal] += 1;
                }
              } else {
                // If there is a record for this day, count if marked true
                if (meals[meal]) counts[day][meal] += 1;
              }
            });
          });
        });

        setMealCounts(counts);
        setUserPoints(points);
        setPointSystem(loadedPoints);
      } catch (err) {
        console.error("Admin data error:", err);
      }
      setLoading(false);
    };

    fetchAll();
  }, []);

  if (loading) return <h2 className="loading">Loading data...</h2>;

  return (
    <div className="admin-container">
      <h2 className="title">üìä Admin Dashboard</h2>

      <h3>ü•ò Meal Counts (Next 8 Days)</h3>
      <table className="meal-table">
        <thead>
          <tr>
            <th className="date-col">Date</th>
            <th>üç≥ Breakfast</th>
            <th>ü•ó Lunch</th>
            <th>üçõ Supper</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(mealCounts).map(([day, meals]) => (
            <tr key={day}>
              <td className="date-col">{day}</td>
              <td>{meals.breakfast}</td>
              <td>{meals.lunch}</td>
              <td>{meals.supper}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>üèÜ Monthly Points</h3>
      <table className="meal-table">
        <thead>
          <tr>
            <th>Student</th>
            <th>Total Points</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(userPoints).map(([email, points]) => (
            <tr key={email}>
              <td>{email}</td>
              <td>{points}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="admin-actions">
        <button className="btn points-btn" onClick={goToPointSystem}>
          ‚öôÔ∏è Point System
        </button>
        <button className="logout-btn" onClick={onLogout}>
          Logout
        </button>
      </div>
    </div>
  );
}

export default AdminPage;
