import React, { useEffect, useState } from "react";
import { auth, db } from "./firebase";
import { doc, getDoc, setDoc } from "firebase/firestore";

function StudentPage() {
  const [meals, setMeals] = useState({});
  const uid = auth.currentUser?.uid;

  useEffect(() => {
    if (!uid) return;

    const fetchMeals = async () => {
      const userMealsRef = doc(db, "userMeals", uid);
      const docSnap = await getDoc(userMealsRef);

      let savedMeals = {};
      if (docSnap.exists()) {
        savedMeals = docSnap.data();
      }

      const today = new Date();
      let upcomingMeals = {};

      for (let i = 0; i < 7; i++) {
        const date = new Date(today);
        date.setDate(today.getDate() + i);
        const dateKey = date.toISOString().split("T")[0];

        // If no record exists in DB → default to true
        if (!savedMeals[dateKey]) {
          savedMeals[dateKey] = {
            breakfast: true,
            lunch: true,
            supper: true,
          };
        }

        upcomingMeals[dateKey] = savedMeals[dateKey];
      }

      setMeals(upcomingMeals);

      // save back any new defaults to Firestore
      await setDoc(userMealsRef, savedMeals, { merge: true });
    };

    fetchMeals();
  }, [uid]);

  const toggleMeal = async (dateKey, mealType) => {
    // check if date is locked
    const today = new Date();
    const cutoff = new Date(dateKey);
    cutoff.setDate(cutoff.getDate() - 1); // previous day
    cutoff.setHours(23, 59, 59, 999);

    if (today > cutoff) {
      alert("You cannot change meals for this day anymore.");
      return;
    }

    const updatedMeals = {
      ...meals,
      [dateKey]: {
        ...meals[dateKey],
        [mealType]: !meals[dateKey][mealType],
      },
    };

    setMeals(updatedMeals);

    const userMealsRef = doc(db, "userMeals", uid);
    await setDoc(userMealsRef, updatedMeals, { merge: true });
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Mark Your Meals (Next 7 Days)</h2>
      {Object.keys(meals).map((dateKey) => {
        const today = new Date();
        const cutoff = new Date(dateKey);
        cutoff.setDate(cutoff.getDate() - 1);
        cutoff.setHours(23, 59, 59, 999);

        const isLocked = today > cutoff;

        return (
          <div key={dateKey} style={{ marginBottom: "15px" }}>
            <h4>
              {dateKey} {isLocked && "(Locked)"}
            </h4>
            {["breakfast", "lunch", "supper"].map((mealType) => (
              <button
                key={mealType}
                onClick={() => toggleMeal(dateKey, mealType)}
                disabled={isLocked}
                style={{
                  margin: "5px",
                  padding: "10px 15px",
                  backgroundColor: meals[dateKey][mealType]
                    ? "lightgreen"
                    : "red",
                  color: "white",
                  border: "none",
                  borderRadius: "8px",
                  cursor: isLocked ? "not-allowed" : "pointer",
                  opacity: isLocked ? 0.6 : 1,
                }}
              >
                {mealType} {meals[dateKey][mealType] ? "✓" : "✗"}
              </button>
            ))}
          </div>
        );
      })}
    </div>
  );
}

export default StudentPage;
