import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { doc, getDoc } from "firebase/firestore";

function AdminPage() {
  const [mealCounts, setMealCounts] = useState({});
  const [next7Days, setNext7Days] = useState([]);

  // üîπ Generate next 7 days
  useEffect(() => {
    const days = [];
    const today = new Date();
    for (let i = 0; i < 7; i++) {
      const d = new Date(today);
      d.setDate(today.getDate() + i);
      const dateStr = d.toISOString().split("T")[0]; // YYYY-MM-DD
      days.push(dateStr);
    }
    setNext7Days(days);
  }, []);

  // üîπ Fetch counts only for next 7 days
  useEffect(() => {
    const fetchCounts = async () => {
      const meals = ["breakfast", "lunch", "supper"];
      const counts = {};

      for (const date of next7Days) {
        counts[date] = {};
        for (const meal of meals) {
          const mealRef = doc(db, "meals", meal, "days", date);
          const mealSnap = await getDoc(mealRef);
          if (mealSnap.exists()) {
            const users = mealSnap.data().users || [];
            counts[date][meal] = users.length;
          } else {
            counts[date][meal] = 0;
          }
        }
      }
      setMealCounts(counts);
    };

    if (next7Days.length > 0) fetchCounts();
  }, [next7Days]);

  return (
    <div style={{ textAlign: "center", marginTop: "30px" }}>
      <h2>üìä Admin Dashboard</h2>
      <h3>Meals Count (Next 7 Days)</h3>

      {next7Days.map((dateStr) => (
        <div
          key={dateStr}
          style={{
            border: "1px solid gray",
            borderRadius: "10px",
            padding: "10px",
            margin: "10px auto",
            maxWidth: "400px",
            backgroundColor: "#f9f9f9",
          }}
        >
          <h4>{dateStr}</h4>
          <p>üç≥ Breakfast: {mealCounts[dateStr]?.breakfast || 0}</p>
          <p>üçõ Lunch: {mealCounts[dateStr]?.lunch || 0}</p>
          <p>ü•ó Supper: {mealCounts[dateStr]?.supper || 0}</p>
        </div>
      ))}
    </div>
  );
}

export default AdminPage;
