import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import {
  doc,
  getDoc,
  setDoc,
  updateDoc,
  serverTimestamp,
} from "firebase/firestore";
import "./StudentPage.css";

function StudentPage({ currentUser, onLogout }) {
  const [mealData, setMealData] = useState({});
  const [loading, setLoading] = useState(true);

  // Generate next 7 days
  const getNext7Days = () => {
    const days = [];
    const today = new Date();
    for (let i = 0; i < 7; i++) {
      const nextDay = new Date(today);
      nextDay.setDate(today.getDate() + i);
      const dateStr = nextDay.toISOString().split("T")[0];
      days.push(dateStr);
    }
    return days;
  };

  const days = getNext7Days();

  // Load meals from Firestore
  useEffect(() => {
    const fetchData = async () => {
      try {
        const userRef = doc(db, "users", currentUser.uid);
        const userSnap = await getDoc(userRef);
        if (userSnap.exists()) {
          setMealData(userSnap.data().meals || {});
        }
      } catch (err) {
        console.error("Error loading meals:", err);
      }
      setLoading(false);
    };
    fetchData();
  }, [currentUser]);

  // Toggle marking
  const toggleMeal = async (day, meal) => {
    const today = new Date().toISOString().split("T")[0];

    // Prevent marking today (locked after midnight)
    if (day === today) {
      alert("‚ùå Marking for today is locked after midnight!");
      return;
    }

    try {
      const newStatus = !(mealData[day]?.[meal] || false);

      const updatedMeals = {
        ...mealData,
        [day]: {
          ...mealData[day],
          [meal]: newStatus,
          lastUpdated: serverTimestamp(),
        },
      };

      setMealData(updatedMeals);

      const userRef = doc(db, "users", currentUser.uid);
      await setDoc(
        userRef,
        {
          meals: updatedMeals,
        },
        { merge: true }
      );
    } catch (err) {
      console.error("Error updating meal:", err);
    }
  };

  if (loading) return <h2 className="loading">Loading meals...</h2>;

  return (
    <div className="student-container">
      <h2 className="title">üçΩÔ∏è Mark Your Meals (Next 7 Days)</h2>
      {days.map((day) => (
        <div key={day} className="day-row">
          <h3 className="day-label">
            {day} {day === new Date().toISOString().split("T")[0] ? "(Locked)" : ""}
          </h3>
          <div className="meal-buttons">
            {["breakfast", "lunch", "supper"].map((meal) => (
              <button
                key={meal}
                className={`meal-btn ${
                  mealData[day]?.[meal] ? "marked" : "unmarked"
                }`}
                onClick={() => toggleMeal(day, meal)}
                disabled={day === new Date().toISOString().split("T")[0]}
              >
                {meal.toUpperCase()}{" "}
                {mealData[day]?.[meal] ? "‚úì" : "‚úó"}
              </button>
            ))}
          </div>
        </div>
      ))}
      <button className="logout-btn" onClick={onLogout}>
        Logout
      </button>
    </div>
  );
}

export default StudentPage;
