import { useState, useEffect } from "react";
import { auth, db, signOut } from "./firebase";
import { onAuthStateChanged } from "firebase/auth";
import { doc, getDoc } from "firebase/firestore";

import LoginPage from "./LoginPage";
import SignupPage from "./SignupPage";
import ResetPage from "./ResetPage";   // ✅ add reset page
import AdminPage from "./AdminPage";
import StudentPage from "./StudentPage";
import PointSystemPage from "./PointSystemPage";

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState("login"); // "login" | "signup" | "reset"

  const [adminView, setAdminView] = useState("dashboard");

  // 🔹 Listen for login state
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      if (user) {
        const docRef = doc(db, "users", user.uid);
        const docSnap = await getDoc(docRef);

        if (docSnap.exists()) {
          const userData = docSnap.data();

          if (userData.status === "pending") {
            alert("⏳ Your account is pending admin approval.");
            await signOut(auth);
            setCurrentUser(null);
            setUserRole(null);
            setLoading(false);
            return;
          }

          if (userData.status === "rejected") {
            alert("❌ Your account request was rejected by admin.");
            await signOut(auth);
            setCurrentUser(null);
            setUserRole(null);
            setLoading(false);
            return;
          }

          setCurrentUser(user);
          setUserRole(userData.role || "student");
        } else {
          setCurrentUser(null);
          setUserRole(null);
        }
      } else {
        setCurrentUser(null);
        setUserRole(null);
        setAdminView("dashboard");
      }
      setLoading(false);
    });
    return () => unsubscribe();
  }, []);

  // 🔹 Handle Logout
  const handleLogout = async () => {
    await signOut(auth);
    setAdminView("dashboard");
  };

  // 🔹 Loading screen
  if (loading) {
    return <h2>Loading...</h2>;
  }

  // 🔹 If not logged in → show Login / Signup / Reset
  if (!currentUser) {
    if (page === "signup") {
      return (
        <SignupPage
          onSignupSuccess={() => setPage("login")}
          goToLogin={() => setPage("login")}
        />
      );
    }
    if (page === "reset") {
      return (
        <ResetPage
          goToLogin={() => setPage("login")}
          goToSignup={() => setPage("signup")}
        />
      );
    }
    return (
      <LoginPage
        onLoginSuccess={() => {}}
        goToSignup={() => setPage("signup")}
        goToReset={() => setPage("reset")}
      />
    );
  }

  // 🔹 If logged in as Admin
  if (userRole === "admin") {
    if (adminView === "points") {
      return <PointSystemPage goBack={() => setAdminView("dashboard")} />;
    }
    return (
      <AdminPage
        onLogout={handleLogout}
        goToPointSystem={() => setAdminView("points")}
      />
    );
  }

  // 🔹 If logged in as Student
  return <StudentPage currentUser={currentUser} onLogout={handleLogout} />;
}

export default App;
