import React, { useState, useEffect } from "react";
import {
  getAuth,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  onAuthStateChanged,
  signOut,
} from "firebase/auth";
import { getFirestore, doc, setDoc, getDoc } from "firebase/firestore";

import StudentPage from "./StudentPage";
import AdminPage from "./AdminPage";
import app from "./firebase"; // your firebase.js config

const auth = getAuth(app);
const db = getFirestore(app);

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [loading, setLoading] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // Watch user auth state
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      if (user) {
        setCurrentUser(user);
        const roleRef = doc(db, "users", user.uid);
        const roleSnap = await getDoc(roleRef);

        if (roleSnap.exists()) {
          setUserRole(roleSnap.data().role);
        } else {
          // ðŸ‘‡ Default role: student
          await setDoc(roleRef, { role: "student" });
          setUserRole("student");
        }
      } else {
        setCurrentUser(null);
        setUserRole(null);
      }
      setLoading(false);
    });
    return () => unsubscribe();
  }, []);

  // Signup
  const handleSignup = async () => {
    try {
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        email,
        password
      );
      const user = userCredential.user;
      await setDoc(doc(db, "users", user.uid), { role: "student" }); // ðŸ‘ˆ always student by default
      setEmail("");
      setPassword("");
    } catch (error) {
      alert(error.message);
    }
  };

  // Login
  const handleLogin = async () => {
    try {
      await signInWithEmailAndPassword(auth, email, password);
      setEmail("");
      setPassword("");
    } catch (error) {
      alert(error.message);
    }
  };

  // Logout
  const handleLogout = async () => {
    await signOut(auth);
  };

  if (loading) return <h2>Loading...</h2>;

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      {!currentUser ? (
        <div>
          <h2>Login / Signup</h2>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <br />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <br />
          <button onClick={handleLogin}>Login</button>
          <button onClick={handleSignup}>Signup</button>
        </div>
      ) : (
        <div>
          <p>
            Logged in as: {currentUser.email} ({userRole})
          </p>
          <button onClick={handleLogout}>Logout</button>

          {/* ðŸ‘‡ Show correct page */}
          {userRole === "student" && <StudentPage user={currentUser} />}
          {userRole === "admin" && <AdminPage />}
        </div>
      )}
    </div>
  );
}

export default App;
