import React, { useState, useEffect } from "react";
import { auth, db } from "./firebase";
import {
  doc,
  getDoc,
  setDoc,
  updateDoc,
  addDoc,
  collection,
  serverTimestamp,
  query,
  orderBy,
  onSnapshot,
} from "firebase/firestore";
import UserProfilePage from "./UserProfilePage";
import "./ModernStudentPage.css";

function ModernStudentPage({ currentUser, onLogout }) {
  const [activeTab, setActiveTab] = useState("home");
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [meals, setMeals] = useState({});
  const [currentMonth, setCurrentMonth] = useState(new Date().getMonth() + 1);
  const [currentYear, setCurrentYear] = useState(new Date().getFullYear());
  const [showProfile, setShowProfile] = useState(false);
  const [suggestion, setSuggestion] = useState("");
  const [showSuggestionForm, setShowSuggestionForm] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [messBill, setMessBill] = useState({
    amount: 0,
    dueDate: "",
    status: "pending",
  });
  const [menuData, setMenuData] = useState({});
  const [mealTimes, setMealTimes] = useState({
    breakfast: "7:00 AM - 9:00 AM",
    lunch: "12:00 PM - 2:00 PM",
    supper: "7:00 PM - 9:00 PM",
  });
  const [feesData, setFeesData] = useState({ months: {}, pendingTotal: 0 });
  const [bunchDetails, setBunchDetails] = useState(null);

  // Load user data and meals
  useEffect(() => {
    const loadUserData = async () => {
      if (!currentUser) return;

      try {
        const userRef = doc(db, "users", currentUser.uid);
        const userSnap = await getDoc(userRef);

        if (userSnap.exists()) {
          setUserData(userSnap.data());
        }

        // Load meals for current month
        const mealsRef = doc(
          db,
          "meals",
          `${currentYear}-${String(currentMonth).padStart(2, "0")}`
        );
        const mealsSnap = await getDoc(mealsRef);

        let mealsData = {};
        if (mealsSnap.exists()) {
          mealsData = mealsSnap.data();
        }

        // Default all future days to marked
        const today = new Date().toISOString().split("T")[0];
        const upcomingDays = getUpcomingDays();

        upcomingDays.forEach((day) => {
          if (!mealsData[day.date]) {
            if (day.date > today) {
              mealsData[day.date] = {
                breakfast: true,
                lunch: true,
                supper: true,
              };
            } else if (day.date < today) {
              mealsData[day.date] = {
                breakfast: false,
                lunch: false,
                supper: false,
              };
            } else {
              mealsData[day.date] = {
                breakfast: true,
                lunch: true,
                supper: true,
              };
            }
          }
        });

        setMeals(mealsData);

        // Save the updated meals data
        await setDoc(mealsRef, mealsData, { merge: true });

        // Load weekday menu data
        const menuRef = doc(db, "weekdayMenu", "default");
        const menuSnap = await getDoc(menuRef);
        if (menuSnap.exists()) setMenuData(menuSnap.data());

        // Load mess bill
        const billRef = doc(db, "messBills", currentUser.uid);
        const billSnap = await getDoc(billRef);
        if (billSnap.exists()) {
          setMessBill(billSnap.data());
        }

        // Load full fees data
        const feesRef = doc(db, "fees", currentUser.uid);
        const feesSnap = await getDoc(feesRef);
        if (feesSnap.exists()) setFeesData(feesSnap.data());

        // Load mess bunch details
        const bunchRef = doc(db, "messBunch", "contacts");
        const bunchSnap = await getDoc(bunchRef);
        if (bunchSnap.exists()) setBunchDetails(bunchSnap.data());
      } catch (error) {
        console.error("Error loading user data:", error);
      } finally {
        setLoading(false);
      }
    };

    loadUserData();
  }, [currentUser, currentMonth, currentYear]);

  // Load notifications
  useEffect(() => {
    const notificationsRef = collection(db, "notifications");
    const q = query(notificationsRef, orderBy("timestamp", "desc"));

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const notificationsData = [];
      snapshot.forEach((doc) => {
        notificationsData.push({ id: doc.id, ...doc.data() });
      });
      setNotifications(notificationsData);
    });

    return () => unsubscribe();
  }, []);

  // Toggle meal selection
  const toggleMeal = async (date, mealType) => {
    if (!currentUser) return;

    const today = new Date().toISOString().split("T")[0];
    if (date === today) {
      alert("❌ Today's marking is locked!");
      return;
    }

    try {
      const newMeals = { ...meals };

      if (!newMeals[date]) {
        newMeals[date] = { breakfast: true, lunch: true, supper: true };
      }

      newMeals[date][mealType] = !newMeals[date][mealType];

      const mealsRef = doc(
        db,
        "meals",
        `${currentYear}-${String(currentMonth).padStart(2, "0")}`
      );
      await setDoc(mealsRef, newMeals, { merge: true });

      setMeals(newMeals);
    } catch (error) {
      console.error("Error updating meal:", error);
    }
  };

  // Generate upcoming days
  const getUpcomingDays = () => {
    const days = [];
    const today = new Date();

    for (let i = 0; i < 14; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() + i);

      days.push({
        date: date.toISOString().split("T")[0],
        dayName: date.toLocaleDateString("en-US", { weekday: "short" }),
        dayNumber: date.getDate(),
        monthName: date.toLocaleDateString("en-US", { month: "short" }),
      });
    }

    return days;
  };

  // Submit suggestion
  const submitSuggestion = async () => {
    if (!suggestion.trim()) {
      alert("Please enter a suggestion");
      return;
    }

    try {
      await addDoc(collection(db, "suggestions"), {
        suggestion: suggestion,
        userName: userData?.name || currentUser?.email || "Anonymous",
        userEmail: currentUser?.email || "unknown",
        timestamp: serverTimestamp(),
      });

      setSuggestion("");
      setShowSuggestionForm(false);
      alert("✅ Suggestion submitted successfully!");
    } catch (error) {
      console.error("Error submitting suggestion:", error);
      alert("❌ Error submitting suggestion");
    }
  };

  if (loading) {
    return (
      <div className="modern-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading...</p>
        </div>
      </div>
    );
  }

  if (showProfile) {
    return (
      <UserProfilePage
        currentUser={currentUser}
        onLogout={onLogout}
        onBack={() => setShowProfile(false)}
      />
    );
  }

  return (
    <div className="modern-container" style={{minHeight:"100vh",display:"grid",gridTemplateColumns:"240px 1fr",background:"linear-gradient(135deg,#7c3aed,#4f46e5 40%, #06b6d4)",padding:"16px"}}>
      {/* Sidebar */}
      <aside style={{background:"linear-gradient(180deg,#6d28d9,#4f46e5)",color:"#fff",borderRadius:16,padding:14,display:"flex",flexDirection:"column",gap:8}}>
        <div style={{display:"flex",alignItems:"center",gap:10,marginBottom:8}}>
          <div style={{width:36,height:36,borderRadius:10,background:"rgba(255,255,255,.25)"}} />
          <div>
            <div style={{fontWeight:700}}>MEA Mess</div>
            <div style={{fontSize:12,opacity:.8}}>Student</div>
          </div>
        </div>
        <button onClick={()=>setActiveTab("home")} style={{textAlign:"left",padding:"10px 12px",borderRadius:12,border:0,background: activeTab==="home"?"#fff":"transparent",color: activeTab==="home"?"#6d28d9":"#fff",cursor:"pointer"}}>🏠 Home</button>
        <button onClick={()=>setActiveTab("meals")} style={{textAlign:"left",padding:"10px 12px",borderRadius:12,border:0,background: activeTab==="meals"?"#fff":"transparent",color: activeTab==="meals"?"#6d28d9":"#fff",cursor:"pointer"}}>🍽️ Mark Meals</button>
        <button onClick={()=>setActiveTab("suggestions")} style={{textAlign:"left",padding:"10px 12px",borderRadius:12,border:0,background: activeTab==="suggestions"?"#fff":"transparent",color: activeTab==="suggestions"?"#6d28d9":"#fff",cursor:"pointer"}}>💡 Suggestions</button>
        <button onClick={()=>setActiveTab("admin")} style={{textAlign:"left",padding:"10px 12px",borderRadius:12,border:0,background: activeTab==="admin"?"#fff":"transparent",color: activeTab==="admin"?"#6d28d9":"#fff",cursor:"pointer"}}>📞 Contact Admin</button>
        <button onClick={()=>setActiveTab("fees")} style={{textAlign:"left",padding:"10px 12px",borderRadius:12,border:0,background: activeTab==="fees"?"#fff":"transparent",color: activeTab==="fees"?"#6d28d9":"#fff",cursor:"pointer"}}>💳 Billing</button>
        <button onClick={()=>setShowProfile(true)} style={{textAlign:"left",padding:"10px 12px",borderRadius:12,border:0,background:"transparent",color:"#fff",cursor:"pointer"}}>⚙️ Settings</button>
        <div style={{marginTop:"auto",display:"grid",gap:8}}>
          <button onClick={onLogout} style={{textAlign:"left",padding:"10px 12px",borderRadius:12,border:0,background:"#fff",color:"#6d28d9",cursor:"pointer"}}>🚪 Logout</button>
        </div>
      </aside>

      {/* Right column */}
      <div style={{background:"#0f172a10",backdropFilter:"blur(6px)",borderRadius:16,overflow:"hidden"}}>
        {/* Topbar */}
        <header style={{background:"#ffffffdd",borderBottom:"1px solid #e2e8f0",display:"flex",alignItems:"center",gap:12,padding:"10px 16px"}}>
          <div style={{margin:"0 auto",width:"min(560px,100%)",background:"#f1f5f9",borderRadius:999,display:"flex",alignItems:"center",gap:10,padding:"8px 14px"}}>
            <span style={{color:"#64748b"}}>🔎</span>
            <input placeholder="Search" style={{border:0,outline:0,background:"transparent",flex:1}}/>
          </div>
          <button style={{background:"transparent",border:0,padding:8,borderRadius:10}}>🔔</button>
          <button style={{background:"transparent",border:0,padding:0}}><img alt="avatar" src="https://i.pravatar.cc/80?img=12" style={{width:34,height:34,borderRadius:999}}/></button>
        </header>

        <div className="main-content">
        {activeTab === "home" && (
          <div className="tab-content">
            <div className="welcome-section">
              <h1 className="app-title">MEA MESS</h1>
              <p className="welcome-subtitle">
                Welcome back, {userData?.name || "Student"}!
              </p>
            </div>

            <div className="profile-card" onClick={() => setShowProfile(true)}>
              <div className="profile-avatar">
                <div className="avatar-circle">
                  {userData?.name
                    ? userData.name.charAt(0).toUpperCase()
                    : "U"}
                </div>
              </div>
              <div className="profile-info">
                <h3>{userData?.name || "User Name"}</h3>
                <p>{userData?.department || "Department"}</p>
                <button className="profile-btn">View Profile</button>
              </div>
            </div>

            <div className="bill-summary">
              <div className="bill-info">
                <h3>Total Pending Mess Bill</h3>
                <p className="bill-amount">₹{messBill.amount || 0}</p>
                <p className="bill-month">Due: {messBill.dueDate || "Not set"}</p>
                <p className="bill-status">
                  Status: {messBill.status || "pending"}
                </p>
              </div>
              <div className="bill-actions">
                <button
                  className="view-bills-btn"
                  onClick={() => setActiveTab("notifications")}
                >
                  <span className="btn-icon">📋</span>
                  View Details
                </button>
              </div>
            </div>

            <div className="feature-grid">
              <div
                className="feature-card"
                onClick={() => setActiveTab("meals")}
              >
                <div className="feature-icon">🍽️</div>
                <h4>Mark Meals</h4>
                <p>Select your meals</p>
              </div>

              <div
                className="feature-card"
                onClick={() => setActiveTab("suggestions")}
              >
                <div className="feature-icon">💡</div>
                <h4>Suggestions</h4>
                <p>Send feedback</p>
              </div>

              <div className="feature-card">
                <div className="feature-icon">👨‍💼</div>
                <h4>Contact Admin</h4>
                <p>Get help</p>
              </div>
            </div>
          </div>
        )}

        {activeTab === "meals" && (
          <div className="tab-content">
            <div className="meals-header">
              <h2>Meal Marking History</h2>
              <p>Your meal selection history with color coding</p>
            </div>

            <div className="meals-calendar">
              {getUpcomingDays().map((day) => {
                const today = new Date().toISOString().split("T")[0];
                const isToday = day.date === today;
                const isPast = day.date < today;

                return (
                  <div
                    key={day.date}
                    className={`day-card ${
                      isPast ? "past" : isToday ? "today" : "future"
                    }`}
                  >
                    <div className="day-info">
                      <div className="day-date">
                        <span className="day-number">{day.dayNumber}</span>
                        <span className="day-month">{day.monthName}</span>
                      </div>
                      <div className="day-name">{day.dayName}</div>
                    </div>

                    <div className="meal-buttons">
                      {["breakfast", "lunch", "supper"].map((mealType) => {
                        const isMarked = meals[day.date]?.[mealType];
                        const mealTime = mealTimes[mealType];

                        return (
                          <button
                            key={mealType}
                            className={`meal-btn ${
                              isMarked ? "marked" : "unmarked"
                            } ${isPast ? "past" : ""} ${
                              isToday ? "locked" : ""
                            }`}
                            onClick={() =>
                              !isPast && !isToday && toggleMeal(day.date, mealType)
                            }
                            disabled={isPast || isToday}
                            title={
                              isPast
                                ? "Past date"
                                : isToday
                                ? "Today - Locked"
                                : `${
                                    mealType.charAt(0).toUpperCase() +
                                    mealType.slice(1)
                                  } - ${mealTime}`
                            }
                          >
                            {mealType.charAt(0).toUpperCase() +
                              mealType.slice(1)}
                            {isMarked ? " ✓" : " ✗"}
                            {isToday ? " 🔒" : ""}
                          </button>
                        );
                      })}
                    </div>
                  </div>
                );
              })}
            </div>

            <div className="legend">
              <h4>Legend:</h4>
              <div className="legend-items">
                <div className="legend-item">
                  <span className="legend-color green"></span>
                  <span>Marked</span>
                </div>
                <div className="legend-item">
                  <span className="legend-color red"></span>
                  <span>Not Marked</span>
                </div>
                <div className="legend-item">
                  <span className="legend-color blue"></span>
                  <span>Today (Locked)</span>
                </div>
                <div className="legend-item">
                  <span className="legend-color gray"></span>
                  <span>Past/Future</span>
                </div>
              </div>
            </div>
          </div>
        )}

        {activeTab === "notifications" && (
          <div className="tab-content">
            <div className="notifications-header">
              <h2>Notifications</h2>
              <p>Stay updated with important announcements</p>
            </div>

            <div className="notifications-list">
              {notifications.map((notification) => (
                <div key={notification.id} className="notification-card">
                  <div className="notification-icon">📢</div>
                  <div className="notification-content">
                    <p className="notification-message">
                      {notification.message}
                    </p>
                    <span className="notification-time">
                      {new Date(
                        notification.timestamp?.toDate?.() ||
                          notification.timestamp
                      ).toLocaleString()}
                    </span>
                  </div>
                </div>
              ))}
              {notifications.length === 0 && (
                <div className="no-notifications">
                  <p>No notifications yet</p>
                </div>
              )}
            </div>

            <div className="mess-bill-section">
              <h3>Mess Bill</h3>
              <div className="bill-card">
                <div className="bill-info">
                  <h4>Current Month Bill</h4>
                  <p>Due Date: {messBill.dueDate || "Not set"}</p>
                </div>
                <div className="bill-amount">
                  <span className="amount">₹{messBill.amount || 0}</span>
                  <span className={`bill-status ${messBill.status}`}>
                    {messBill.status || "pending"}
                  </span>
                </div>
              </div>
            </div>
          </div>
        )}

        {activeTab === "menu" && (
          <div className="tab-content">
            <div className="menu-header">
              <h2>Weekday Menu</h2>
              <p>Fixed menu for each weekday</p>
            </div>

            <div className="menu-schedule">
              {[
                "Monday",
                "Tuesday",
                "Wednesday",
                "Thursday",
                "Friday",
                "Saturday",
                "Sunday",
              ].map((weekday) => (
                <div key={weekday} className="meal-schedule-item">
                  <div className="meal-time">
                    <span className="meal-icon">📅</span>
                    <div className="meal-details">
                      <h4>{weekday}</h4>
                      <p>Breakfast, Lunch, Supper</p>
                    </div>
                  </div>
                  <div className="menu-item">
                    <div>
                      <strong>Breakfast:</strong>{" "}
                      {menuData[weekday]?.breakfast || "Not added yet"}
                    </div>
                    <div>
                      <strong>Lunch:</strong>{" "}
                      {menuData[weekday]?.lunch || "Not added yet"}
                    </div>
                    <div>
                      <strong>Supper:</strong>{" "}
                      {menuData[weekday]?.supper || "Not added yet"}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === "suggestions" && (
          <div className="tab-content">
            <div className="suggestions-header">
              <h2>Send Feedback</h2>
              <p>Help us improve the mess experience</p>
            </div>

            <div className="suggestions-form">
              <textarea
                className="feedback-input"
                placeholder="Share your suggestions, complaints, or feedback..."
                rows="6"
                value={suggestion}
                onChange={(e) => setSuggestion(e.target.value)}
              />
              <button className="submit-btn" onClick={submitSuggestion}>
                <span className="btn-icon">📤</span>
                Send Feedback
              </button>
            </div>
          </div>
        )}

        {activeTab === "fees" && (
          <div className="tab-content">
            <div className="notifications-header">
              <h2>Fees</h2>
              <p>Your monthly fees: paid and pending</p>
            </div>
            <div className="mess-bill-section">
              <div className="bill-card">
                <div className="bill-info">
                  <h4>Total Pending</h4>
                  <p>Updated monthly</p>
                </div>
                <div className="bill-amount">
                  <span className="amount">₹{feesData?.pendingTotal || 0}</span>
                </div>
              </div>
            </div>
            <div className="notifications-list">
              {Object.entries(feesData?.months || {})
                .sort(([a], [b]) => a.localeCompare(b))
                .map(([monthKey, data]) => (
                  <div key={monthKey} className="notification-card">
                    <div className="notification-icon">📅</div>
                    <div className="notification-content">
                      <p className="notification-message">{monthKey}</p>
                      <span className="notification-time">
                        Fee: ₹{data.fee || 0} • Paid: ₹{data.paid || 0} •
                        Pending: ₹{Math.max((data.fee || 0) - (data.paid || 0), 0)}
                      </span>
                    </div>
                  </div>
                ))}
              {Object.keys(feesData?.months || {}).length === 0 && (
                <div className="no-notifications">
                  <p>No fee records yet</p>
                </div>
              )}
            </div>
          </div>
        )}

        {activeTab === "admin" && (
          <div className="tab-content">
            <div className="notifications-header">
              <h2>Admin Details</h2>
              <p>MEA Mess Bunch</p>
            </div>
            <div className="notifications-list">
              {bunchDetails ? (
                [
                  { key: "cmd", label: "CMD (Chief Mess Director)" },
                  { key: "acmd", label: "ACMD (Assistant CMD)" },
                  { key: "juniorAcmd", label: "Junior ACMD" },
                  { key: "artsSports", label: "Arts & Sports Secretary" },
                  { key: "libraryCoordinator", label: "Library Coordinator" },
                ].map((f) => (
                  <div key={f.key} className="notification-card">
                    <div className="notification-icon">👤</div>
                    <div className="notification-content">
                      <p className="notification-message">{f.label}</p>
                      <span className="notification-time">
                        {bunchDetails?.[f.key] || "Not set"}
                      </span>
                    </div>
                  </div>
                ))
              ) : (
                <div className="no-notifications">
                  <p>No details available</p>
                </div>
              )}
            </div>
          </div>
        )}
        </div>
      </div>
    </div>
  );
}

export default ModernStudentPage;
