import { useState } from "react";
import { auth, db, createUserWithEmailAndPassword } from "./firebase";
import { doc, setDoc, serverTimestamp } from "firebase/firestore";
import "./signuppage.css";

function SignupPage({ onSignupSuccess, goToLogin }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // Generate next 7 days
  const getNext7Days = () => {
    const days = [];
    const today = new Date();
    for (let i = 0; i < 7; i++) {
      const nextDay = new Date(today);
      nextDay.setDate(today.getDate() + i);
      const dateStr = nextDay.toISOString().split("T")[0];
      days.push(dateStr);
    }
    return days;
  };

  // Create default meal plan
  const createDefaultMeals = () => {
    const meals = {};
    const todayStr = new Date().toISOString().split("T")[0];
    const days = getNext7Days();

    days.forEach((day) => {
      if (day > todayStr) {
        meals[day] = {
          breakfast: true,
          lunch: true,
          supper: true,
          lastUpdated: serverTimestamp(),
        };
      } else {
        meals[day] = {
          breakfast: false,
          lunch: false,
          supper: false,
          lastUpdated: serverTimestamp(),
        };
      }
    });

    return meals;
  };

  // Handle Signup
  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);

      // Save to Firestore with role + default meals
      await setDoc(doc(db, "users", userCredential.user.uid), {
        email,
        role: "student",
        meals: createDefaultMeals(),
      });

      alert("✅ Signup successful!");
      if (onSignupSuccess) onSignupSuccess();
    } catch (err) {
      alert("⚠️ " + err.message);
    }
  };

  return (
    <div className="signup-container">
      <form className="signup-box" onSubmit={handleSignup}>
        <h2 className="signup-title">Sign Up</h2>
        <input
          type="email"
          className="input-field"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          className="input-field"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" className="btn">Sign Up</button>

        <p className="link">
          Already have an account?{" "}
          <span onClick={goToLogin}>Login</span>
        </p>
      </form>
    </div>
  );
}

export default SignupPage;
