import { useState } from "react";
import { auth, signInWithEmailAndPassword, sendPasswordResetEmail } from "./firebase";
import "./loginpage.css";

function LoginPage({ onLoginSuccess, goToSignup }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // üîπ Handle Login
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, password);
      onLoginSuccess();
    } catch (err) {
      alert("‚ö†Ô∏è " + err.message);
    }
  };

  // üîπ Handle Forgot Password
  const handleForgotPassword = async () => {
    if (!email) {
      alert("Please enter your email first!");
      return;
    }
    try {
      await sendPasswordResetEmail(auth, email);
      alert("üì© Password reset email sent!");
    } catch (err) {
      alert("‚ö†Ô∏è " + err.message);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2 className="auth-title">Login</h2>
        <form onSubmit={handleLogin} className="auth-form">
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" className="auth-btn">Login</button>
        </form>

        <p className="auth-text">
          <button type="button" className="link-btn" onClick={handleForgotPassword}>
            Forgot Password?
          </button>
        </p>
        <p className="auth-text">
          Don‚Äôt have an account?{" "}
          <button className="link-btn" onClick={goToSignup}>Sign Up</button>
        </p>
      </div>
    </div>
  );
}

export default LoginPage;
