import React, { useState } from "react";
import { db } from "./firebase";
import { collection, doc, setDoc, deleteDoc } from "firebase/firestore";

function App() {
  const [meals, setMeals] = useState({
    breakfast: false,
    lunch: false,
    supper: false,
  });

  // Handle toggling meals
  const toggleMeal = async (meal) => {
    const updated = { ...meals, [meal]: !meals[meal] };
    setMeals(updated);

    const mealRef = doc(collection(db, "meals"), meal);

    if (updated[meal]) {
      // Marked → Save in Firestore
      await setDoc(mealRef, { marked: true });
    } else {
      // Unmarked → Remove from Firestore
      await deleteDoc(mealRef);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Mess App</h1>
      <button
        onClick={() => toggleMeal("breakfast")}
        style={{
          backgroundColor: meals.breakfast ? "green" : "gray",
          color: "white",
          margin: "5px",
        }}
      >
        {meals.breakfast ? "Unmark Breakfast" : "Mark Breakfast"}
      </button>
      <button
        onClick={() => toggleMeal("lunch")}
        style={{
          backgroundColor: meals.lunch ? "green" : "gray",
          color: "white",
          margin: "5px",
        }}
      >
        {meals.lunch ? "Unmark Lunch" : "Mark Lunch"}
      </button>
      <button
        onClick={() => toggleMeal("supper")}
        style={{
          backgroundColor: meals.supper ? "green" : "gray",
          color: "white",
          margin: "5px",
        }}
      >
        {meals.supper ? "Unmark Supper" : "Mark Supper"}
      </button>
    </div>
  );
}

export default App;
