// AdminPage.js
import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { collection, getDocs, doc, getDoc, setDoc } from "firebase/firestore";
import "./AdminPage.css";

function AdminPage({ onLogout, goToPointSystem }) {
  const [mealCounts, setMealCounts] = useState({});
  const [userPoints, setUserPoints] = useState({});
  const [loading, setLoading] = useState(true);

  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [generated, setGenerated] = useState(false);

  // default rules (admin default)
  const defaultSystem = {
    X: 1,     // all marked
    "0": 0,   // none marked
    B: 0.25,
    L: 0.35,
    S: 0.45,
    "B/L": 0.65,
    "B/S": 0.75,
    "L/S": 0.85,
  };

  const [pointSystem, setPointSystem] = useState(() => normalizePointSystem(defaultSystem));
  const [allUsers, setAllUsers] = useState([]);

  // --- helpers ---------------------------------------------------------------
  // canonicalizes keys so pair orders become consistent (B/L not L/B)
  const canonicalKey = (k) => {
    if (!k || typeof k !== "string") return k;
    if (k === "X" || k === "0") return k;
    if (k.includes("/")) {
      const order = ["B", "L", "S"];
      const parts = k.split("/").map((p) => p.trim().toUpperCase());
      parts.sort((a, b) => order.indexOf(a) - order.indexOf(b));
      return parts.join("/");
    }
    return k.toUpperCase();
  };

  // Normalize an object of key->value (strings or numbers) into numeric canonical keys
  function normalizePointSystem(raw = {}) {
    const base = { ...defaultSystem, ...raw }; // fallback to defaults
    const norm = {};
    Object.keys(base).forEach((k) => {
      const ck = canonicalKey(k);
      const v = base[k];
      const num = typeof v === "number" ? v : parseFloat(v);
      norm[ck] = isNaN(num) ? 0 : num;
    });
    // ensure canonical pair keys exist (in case admin saved reversed order)
    ["B/L", "B/S", "L/S"].forEach((k) => {
      if (norm[k] === undefined) {
        const rev = k.split("/").reverse().join("/");
        norm[k] = norm[rev] ?? norm[k] ?? 0;
      }
    });
    // ensure X and 0 exist
    norm["X"] = norm["X"] ?? defaultSystem["X"];
    norm["0"] = norm["0"] ?? defaultSystem["0"];
    return norm;
  }

  // --- load settings + users + meal counts (next 8 days) --------------------
  useEffect(() => {
    const fetchAll = async () => {
      try {
        // load point system from Firestore and normalize
        const settingsRef = doc(db, "settings", "points");
        const settingsSnap = await getDoc(settingsRef);
        let loadedSettings = null;
        if (settingsSnap.exists()) {
          loadedSettings = settingsSnap.data();
        } else {
          // create default record if missing
          await setDoc(settingsRef, defaultSystem);
          loadedSettings = defaultSystem;
        }
        const normalized = normalizePointSystem(loadedSettings);
        setPointSystem(normalized);

        // load all users
        const usersRef = collection(db, "users");
        const querySnapshot = await getDocs(usersRef);

        const all = [];
        const counts = {};
        const today = new Date();

        // next 8 days (today + 7)
        const nextDays = [];
        for (let i = 0; i < 8; i++) {
          const d = new Date(today);
          d.setDate(today.getDate() + i);
          const dateStr = d.toISOString().split("T")[0];
          nextDays.push(dateStr);
          counts[dateStr] = { breakfast: 0, lunch: 0, supper: 0 };
        }

        querySnapshot.forEach((docSnap) => {
          const data = docSnap.data();
          all.push(data);

          const userMeals = data.meals || {};
          nextDays.forEach((day) => {
            const meals = userMeals[day];
            if (meals) {
              // if user explicitly stored that day, count stored values
              ["breakfast", "lunch", "supper"].forEach((meal) => {
                if (meals[meal]) counts[day][meal] += 1;
              });
            } else {
              // if user has no stored entry for that day:
              // ‚Äî treat future days as MARKED (count them), as per your requirement
              // ‚Äî past/today default handled elsewhere (students cannot change past/today)
              const todayStr = new Date().toISOString().split("T")[0];
              if (day > todayStr) {
                counts[day].breakfast += 1;
                counts[day].lunch += 1;
                counts[day].supper += 1;
              }
            }
          });
        });

        setAllUsers(all);
        setMealCounts(counts);
      } catch (err) {
        console.error("Admin data error:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchAll();
  }, []); // run once

  // --- generate report for selected date range -------------------------------
  const generateReport = () => {
    if (!fromDate || !toDate) {
      alert("‚ö†Ô∏è Please select both From and To dates!");
      return;
    }

    const points = {};
    const todayStr = new Date().toISOString().split("T")[0];

    // iterate users
    allUsers.forEach((user) => {
      const email = user.email || "Unknown";
      const userMeals = user.meals || {};
      let totalPoints = 0;

      // iterate days in range inclusive
      const start = new Date(fromDate);
      const end = new Date(toDate);
      start.setHours(0, 0, 0, 0);
      end.setHours(0, 0, 0, 0);

      for (let d = new Date(start); d <= end; d.setDate(d.getDate() + 1)) {
        const day = d.toISOString().split("T")[0];

        // If day entry is missing in Firestore, apply default:
        // - future days (relative to today) => assume MARKED (true)
        // - past & today => assume UNMARKED (false)
        let meals = userMeals[day];
        if (!meals) {
          meals = day > todayStr
            ? { breakfast: true, lunch: true, supper: true }
            : { breakfast: false, lunch: false, supper: false };
        }

        // Build key from MARKED meals in canonical order B, L, S
        const marked = {
          B: !!meals.breakfast,
          L: !!meals.lunch,
          S: !!meals.supper,
        };
        const markedArr = ["B", "L", "S"].filter((m) => marked[m]);
        let key;
        if (markedArr.length === 3) key = "X"; // all marked
        else if (markedArr.length === 0) key = "0"; // none marked
        else key = markedArr.join("/"); // e.g. "B/L", "B/S", "L/S" (canonical order)

        const addVal = pointSystem[key] ?? defaultSystem[key] ?? 0;
        totalPoints += addVal;
      }

      points[email] = totalPoints.toFixed(2);
    });

    setUserPoints(points);
    setGenerated(true);
  };

  // --- export CSV -----------------------------------------------------------
  const exportCSV = () => {
    const rows = [["Email", "Points"]];
    Object.entries(userPoints).forEach(([email, pts]) => {
      rows.push([email, pts]);
    });

    const csvContent = rows.map((r) => r.join(",")).join("\n");
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });

    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.setAttribute("download", `points_${fromDate}_to_${toDate}.csv`);
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  if (loading) return <h2 className="loading">Loading data...</h2>;

  // --- render ----------------------------------------------------------------
  return (
    <div className="admin-container">
      <h2 className="title">üìä Admin Dashboard</h2>

      <h3>ü•ò Meal Counts (Next 8 Days)</h3>
      <table className="meal-table">
        <thead>
          <tr>
            <th className="date-col">Date</th>
            <th>üç≥ Breakfast</th>
            <th>ü•ó Lunch</th>
            <th>üçõ Supper</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(mealCounts).map(([day, meals]) => (
            <tr key={day}>
              <td className="date-col">{day}</td>
              <td className={day === new Date().toISOString().split("T")[0] ? "today-col" : ""}>
                {meals.breakfast}
              </td>
              <td className={day === new Date().toISOString().split("T")[0] ? "today-col" : ""}>
                {meals.lunch}
              </td>
              <td className={day === new Date().toISOString().split("T")[0] ? "today-col" : ""}>
                {meals.supper}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>Generate Points Report</h3>
      <div className="report-filters">
        <label>
          From:{" "}
          <input type="date" value={fromDate} onChange={(e) => setFromDate(e.target.value)} />
        </label>
        <label>
          To:{" "}
          <input type="date" value={toDate} onChange={(e) => setToDate(e.target.value)} />
        </label>
        <button className="btn generate-btn" onClick={generateReport}>
          Generate
        </button>
      </div>

      {generated && (
        <>
          <table className="meal-table">
            <thead>
              <tr>
                <th>Email</th>
                <th>Total Points</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(userPoints).map(([email, points]) => (
                <tr key={email}>
                  <td>{email}</td>
                  <td>{points}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <button className="btn export-btn" onClick={exportCSV}>
            ‚¨áÔ∏è Download CSV
          </button>
        </>
      )}

      <div className="admin-actions">
        <button className="btn points-btn" onClick={goToPointSystem}>
          ‚öôÔ∏è Point System
        </button>
        <button className="logout-btn" onClick={onLogout}>
          Logout
        </button>
      </div>
    </div>
  );
}

export default AdminPage;
