import React, { useState, useEffect } from "react";
import { auth, db } from "./firebase";
import { doc, getDoc, setDoc, updateDoc, addDoc, collection, serverTimestamp, query, orderBy, onSnapshot } from "firebase/firestore";
import UserProfilePage from "./UserProfilePage";
import "./ModernStudentPage.css";

function ModernStudentPage({ currentUser, onLogout }) {
  const [activeTab, setActiveTab] = useState("home");
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [meals, setMeals] = useState({});
  const [currentMonth, setCurrentMonth] = useState(new Date().getMonth() + 1);
  const [currentYear, setCurrentYear] = useState(new Date().getFullYear());
  const [showProfile, setShowProfile] = useState(false);
  const [suggestion, setSuggestion] = useState("");
  const [showSuggestionForm, setShowSuggestionForm] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [messBill, setMessBill] = useState({ amount: 0, dueDate: "", status: "pending" });
  const [menuData, setMenuData] = useState({});
  const [mealTimes, setMealTimes] = useState({
    breakfast: "7:00 AM - 9:00 AM",
    lunch: "12:00 PM - 2:00 PM", 
    supper: "7:00 PM - 9:00 PM"
  });

  // Load user data and meals
  useEffect(() => {
    const loadUserData = async () => {
      if (!currentUser) return;
      
      try {
        const userRef = doc(db, "users", currentUser.uid);
        const userSnap = await getDoc(userRef);
        
        if (userSnap.exists()) {
          setUserData(userSnap.data());
        }
        
        // Load meals for current month
        const mealsRef = doc(db, "meals", `${currentYear}-${String(currentMonth).padStart(2, '0')}`);
        const mealsSnap = await getDoc(mealsRef);
        
        let mealsData = {};
        if (mealsSnap.exists()) {
          mealsData = mealsSnap.data();
        }
        
        // Default all future days to marked
        const today = new Date().toISOString().split('T')[0];
        const upcomingDays = getUpcomingDays();
        
        upcomingDays.forEach(day => {
          if (!mealsData[day.date]) {
            if (day.date > today) {
              // Future days default to marked
              mealsData[day.date] = { breakfast: true, lunch: true, supper: true };
            } else if (day.date < today) {
              // Past days default to unmarked
              mealsData[day.date] = { breakfast: false, lunch: false, supper: false };
            } else {
              // Today defaults to marked but locked
              mealsData[day.date] = { breakfast: true, lunch: true, supper: true };
            }
          }
        });
        
        setMeals(mealsData);
        
        // Save the updated meals data
        await setDoc(mealsRef, mealsData, { merge: true });

        // Load menu data
        const menuRef = doc(db, "menu", `${currentYear}-${String(currentMonth).padStart(2, '0')}`);
        const menuSnap = await getDoc(menuRef);
        
        if (menuSnap.exists()) {
          setMenuData(menuSnap.data());
        }

        // Load mess bill
        const billRef = doc(db, "messBills", currentUser.uid);
        const billSnap = await getDoc(billRef);
        
        if (billSnap.exists()) {
          setMessBill(billSnap.data());
        }
      } catch (error) {
        console.error("Error loading user data:", error);
      } finally {
        setLoading(false);
      }
    };

    loadUserData();
  }, [currentUser, currentMonth, currentYear]);

  // Load notifications
  useEffect(() => {
    const loadNotifications = () => {
      const notificationsRef = collection(db, "notifications");
      const q = query(notificationsRef, orderBy("timestamp", "desc"));
      
      const unsubscribe = onSnapshot(q, (snapshot) => {
        const notificationsData = [];
        snapshot.forEach((doc) => {
          notificationsData.push({ id: doc.id, ...doc.data() });
        });
        setNotifications(notificationsData);
      });

      return unsubscribe;
    };

    const unsubscribe = loadNotifications();
    return () => unsubscribe();
  }, []);

  // Toggle meal selection
  const toggleMeal = async (date, mealType) => {
    if (!currentUser) return;
    
    // Lock today's marking
    const today = new Date().toISOString().split('T')[0];
    if (date === today) {
      alert("❌ Today's marking is locked!");
      return;
    }
    
    try {
      const mealKey = `${date}-${mealType}`;
      const newMeals = { ...meals };
      
      if (!newMeals[date]) {
        // Default to all meals marked for future dates
        newMeals[date] = { breakfast: true, lunch: true, supper: true };
      }
      
      newMeals[date][mealType] = !newMeals[date][mealType];
      
      const mealsRef = doc(db, "meals", `${currentYear}-${String(currentMonth).padStart(2, '0')}`);
      await setDoc(mealsRef, newMeals, { merge: true });
      
      setMeals(newMeals);
    } catch (error) {
      console.error("Error updating meal:", error);
    }
  };

  // Generate upcoming days for meals
  const getUpcomingDays = () => {
    const days = [];
    const today = new Date();
    
    for (let i = 0; i < 14; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() + i);
      
      days.push({
        date: date.toISOString().split('T')[0],
        dayName: date.toLocaleDateString('en-US', { weekday: 'short' }),
        dayNumber: date.getDate(),
        monthName: date.toLocaleDateString('en-US', { month: 'short' })
      });
    }
    
    return days;
  };

  // Submit suggestion
  const submitSuggestion = async () => {
    if (!suggestion.trim()) {
      alert("Please enter a suggestion");
      return;
    }

    try {
      await addDoc(collection(db, "suggestions"), {
        suggestion: suggestion,
        userName: userData?.name || currentUser?.email || "Anonymous",
        userEmail: currentUser?.email || "unknown",
        timestamp: serverTimestamp()
      });

      setSuggestion("");
      setShowSuggestionForm(false);
      alert("✅ Suggestion submitted successfully!");
    } catch (error) {
      console.error("Error submitting suggestion:", error);
      alert("❌ Error submitting suggestion");
    }
  };

  // Generate monthly payments data
  const getMonthlyPayments = () => {
    const months = [];
    const currentDate = new Date();
    
    for (let i = 0; i < 12; i++) {
      const date = new Date(currentDate.getFullYear(), currentDate.getMonth() - i, 1);
      const monthName = date.toLocaleDateString('en-US', { month: 'long' });
      const year = date.getFullYear();
      
      months.push({
        month: monthName,
        year: year,
        amount: Math.floor(Math.random() * 2000) + 1000, // Mock data
        isPaid: Math.random() > 0.5
      });
    }
    
    return months;
  };

  if (loading) {
    return (
      <div className="modern-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading...</p>
        </div>
      </div>
    );
  }

  // Show profile page when requested
  if (showProfile) {
    return <UserProfilePage currentUser={currentUser} onLogout={onLogout} onBack={() => setShowProfile(false)} />;
  }

  return (
    <div className="modern-container">
      {/* Main Content Area */}
      <div className="main-content">
        {activeTab === "home" && (
          <div className="tab-content">
            <div className="welcome-section">
              <h1 className="app-title">MEA MESS</h1>
              <p className="welcome-subtitle">Welcome back, {userData?.name || 'Student'}!</p>
            </div>

            <div className="profile-card" onClick={() => setShowProfile(true)}>
              <div className="profile-avatar">
                <div className="avatar-circle">
                  {userData?.name ? userData.name.charAt(0).toUpperCase() : 'U'}
                </div>
              </div>
              <div className="profile-info">
                <h3>{userData?.name || 'User Name'}</h3>
                <p>{userData?.department || 'Department'}</p>
                <button className="profile-btn">View Profile</button>
              </div>
            </div>

            <div className="bill-summary">
              <div className="bill-info">
                <h3>Current Bill</h3>
                <p className="bill-amount">₹2,450</p>
                <p className="bill-month">December 2024</p>
              </div>
              <button className="pay-bills-btn">
                <span className="btn-icon">💳</span>
                Pay Your Bills
              </button>
            </div>

            <div className="feature-grid">
              <div className="feature-card" onClick={() => setActiveTab("meals")}>
                <div className="feature-icon">🍽️</div>
                <h4>Mark Meals</h4>
                <p>Select your meals</p>
              </div>
              
              <div className="feature-card" onClick={() => setActiveTab("payments")}>
                <div className="feature-icon">💳</div>
                <h4>Pay Bills</h4>
                <p>View payments</p>
              </div>
              
              <div className="feature-card" onClick={() => setActiveTab("suggestions")}>
                <div className="feature-icon">💡</div>
                <h4>Suggestions</h4>
                <p>Send feedback</p>
              </div>
              
              <div className="feature-card">
                <div className="feature-icon">👨‍💼</div>
                <h4>Contact Admin</h4>
                <p>Get help</p>
              </div>
            </div>
          </div>
        )}

        {activeTab === "meals" && (
          <div className="tab-content">
            <div className="meals-header">
              <h2>Meal Marking History</h2>
              <p>Your meal selection history with color coding</p>
            </div>

            <div className="meals-calendar">
              {getUpcomingDays().map((day) => {
                const today = new Date().toISOString().split('T')[0];
                const isFuture = day.date > today;
                const isToday = day.date === today;
                const isPast = day.date < today;
                
                return (
                  <div key={day.date} className={`day-card ${isPast ? 'past' : isToday ? 'today' : 'future'}`}>
                    <div className="day-info">
                      <div className="day-date">
                        <span className="day-number">{day.dayNumber}</span>
                        <span className="day-month">{day.monthName}</span>
                      </div>
                      <div className="day-name">{day.dayName}</div>
                    </div>
                    
                    <div className="meal-buttons">
                      {['breakfast', 'lunch', 'supper'].map((mealType) => {
                        const isMarked = meals[day.date]?.[mealType];
                        const mealTime = mealTimes[mealType];
                        
                        const isToday = day.date === today;
                        
                        return (
                          <button
                            key={mealType}
                            className={`meal-btn ${isMarked ? 'marked' : 'unmarked'} ${isPast ? 'past' : ''} ${isToday ? 'locked' : ''}`}
                            onClick={() => !isPast && !isToday && toggleMeal(day.date, mealType)}
                            disabled={isPast || isToday}
                            title={isPast ? 'Past date' : isToday ? 'Today - Locked' : `${mealType.charAt(0).toUpperCase() + mealType.slice(1)} - ${mealTime}`}
                          >
                            {mealType.charAt(0).toUpperCase() + mealType.slice(1)}
                            {isMarked ? ' ✓' : ' ✗'}
                            {isToday ? ' 🔒' : ''}
                          </button>
                        );
                      })}
                    </div>
                  </div>
                );
              })}
            </div>

            <div className="legend">
              <h4>Legend:</h4>
              <div className="legend-items">
                <div className="legend-item">
                  <span className="legend-color green"></span>
                  <span>Marked</span>
                </div>
                <div className="legend-item">
                  <span className="legend-color red"></span>
                  <span>Not Marked</span>
                </div>
                <div className="legend-item">
                  <span className="legend-color gray"></span>
                  <span>Future/Past</span>
                </div>
              </div>
            </div>
          </div>
        )}

        {activeTab === "notifications" && (
          <div className="tab-content">
            <div className="notifications-header">
              <h2>Notifications</h2>
              <p>Stay updated with important announcements</p>
            </div>

            <div className="notifications-list">
              {notifications.map((notification) => (
                <div key={notification.id} className="notification-card">
                  <div className="notification-icon">📢</div>
                  <div className="notification-content">
                    <p className="notification-message">{notification.message}</p>
                    <span className="notification-time">
                      {new Date(notification.timestamp?.toDate?.() || notification.timestamp).toLocaleString()}
                    </span>
                  </div>
                </div>
              ))}
              {notifications.length === 0 && (
                <div className="no-notifications">
                  <p>No notifications yet</p>
                </div>
              )}
            </div>

            {/* Mess Bill Section */}
            <div className="mess-bill-section">
              <h3>Mess Bill</h3>
              <div className="bill-card">
                <div className="bill-info">
                  <h4>Current Month Bill</h4>
                  <p>Due Date: {messBill.dueDate || "Not set"}</p>
                </div>
                <div className="bill-amount">
                  <span className="amount">₹{messBill.amount || 0}</span>
                  <span className={`bill-status ${messBill.status}`}>
                    {messBill.status || "pending"}
                  </span>
                </div>
              </div>
            </div>
          </div>
        )}

        {activeTab === "menu" && (
          <div className="tab-content">
            <div className="menu-header">
              <h2>Daily Menu</h2>
              <p>Today's meal schedule and menu</p>
            </div>

            <div className="menu-schedule">
              {Object.entries(mealTimes).map(([mealType, time]) => (
                <div key={mealType} className="meal-schedule-item">
                  <div className="meal-time">
                    <span className="meal-icon">
                      {mealType === 'breakfast' ? '🍳' : mealType === 'lunch' ? '🥗' : '🍛'}
                    </span>
                    <div className="meal-details">
                      <h4>{mealType.charAt(0).toUpperCase() + mealType.slice(1)}</h4>
                      <p>{time}</p>
                    </div>
                  </div>
                  <div className="menu-item">
                    {menuData[new Date().toISOString().split('T')[0]]?.[mealType] || "Not added yet"}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === "suggestions" && (
          <div className="tab-content">
            <div className="suggestions-header">
              <h2>Send Feedback</h2>
              <p>Help us improve the mess experience</p>
            </div>

            <div className="suggestions-form">
              <textarea
                className="feedback-input"
                placeholder="Share your suggestions, complaints, or feedback..."
                rows="6"
                value={suggestion}
                onChange={(e) => setSuggestion(e.target.value)}
              />
              <button className="submit-btn" onClick={submitSuggestion}>
                <span className="btn-icon">📤</span>
                Send Feedback
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Fixed Bottom Navigation */}
      <div className="bottom-nav">
        <button
          className={`nav-item ${activeTab === 'home' ? 'active' : ''}`}
          onClick={() => setActiveTab('home')}
        >
          <span className="nav-icon">🏠</span>
          <span className="nav-label">Home</span>
        </button>
        
        <button
          className={`nav-item ${activeTab === 'meals' ? 'active' : ''}`}
          onClick={() => setActiveTab('meals')}
        >
          <span className="nav-icon">🍽️</span>
          <span className="nav-label">Meals</span>
        </button>
        
        <button
          className={`nav-item ${activeTab === 'notifications' ? 'active' : ''}`}
          onClick={() => setActiveTab('notifications')}
        >
          <span className="nav-icon">🔔</span>
          <span className="nav-label">Notifications</span>
        </button>
        
        <button
          className={`nav-item ${activeTab === 'menu' ? 'active' : ''}`}
          onClick={() => setActiveTab('menu')}
        >
          <span className="nav-icon">🍽️</span>
          <span className="nav-label">Menu</span>
        </button>
        
        <button
          className={`nav-item ${activeTab === 'suggestions' ? 'active' : ''}`}
          onClick={() => setActiveTab('suggestions')}
        >
          <span className="nav-icon">💡</span>
          <span className="nav-label">Suggestions</span>
        </button>
      </div>
    </div>
  );
}

export default ModernStudentPage;
