import React, { useState } from "react";

function App() {
  const [user, setUser] = useState(null); // null = not logged in
  const [meals, setMeals] = useState({}); // { username: { breakfast: true, lunch: false, supper: true } }

  // Local login system
  const handleLogin = (role) => {
    if (role === "student") {
      const name = prompt("Enter your name:");
      if (name) {
        setUser({ name, role: "student" });
        if (!meals[name]) {
          setMeals((prev) => ({
            ...prev,
            [name]: { breakfast: false, lunch: false, supper: false },
          }));
        }
      }
    } else {
      // Admin login (fixed account)
      const password = prompt("Enter admin password:");
      if (password === "admin123") {
        setUser({ name: "Admin", role: "admin" });
      } else {
        alert("Wrong password!");
      }
    }
  };

  // Toggle student meal
  const toggleMeal = (meal) => {
    if (!user || user.role !== "student") return;
    setMeals((prev) => ({
      ...prev,
      [user.name]: {
        ...prev[user.name],
        [meal]: !prev[user.name][meal],
      },
    }));
  };

  // Count total meals
  const getMealCount = (mealType) => {
    return Object.values(meals).filter((m) => m[mealType]).length;
  };

  if (!user) {
    // Login page
    return (
      <div style={{ textAlign: "center", marginTop: "50px" }}>
        <h1>Mess App</h1>
        <p>Select login type:</p>
        <button
          onClick={() => handleLogin("student")}
          style={{ margin: "10px", padding: "10px 20px" }}
        >
          Student Login
        </button>
        <button
          onClick={() => handleLogin("admin")}
          style={{ margin: "10px", padding: "10px 20px" }}
        >
          Admin Login
        </button>
      </div>
    );
  }

  if (user.role === "student") {
    // Student page
    const studentMeals = meals[user.name] || {};
    return (
      <div style={{ textAlign: "center", marginTop: "50px" }}>
        <h1>Welcome, {user.name}</h1>
        <p>Toggle your meals:</p>
        {["breakfast", "lunch", "supper"].map((meal) => (
          <button
            key={meal}
            onClick={() => toggleMeal(meal)}
            style={{
              margin: "10px",
              padding: "10px 20px",
              background: studentMeals[meal] ? "green" : "gray",
              color: "white",
              border: "none",
              borderRadius: "5px",
            }}
          >
            {studentMeals[meal] ? `‚úì ${meal} Marked` : `Mark ${meal}`}
          </button>
        ))}
      </div>
    );
  }

  if (user.role === "admin") {
    // Admin page
    return (
      <div style={{ textAlign: "center", marginTop: "50px" }}>
        <h1>Admin Dashboard</h1>
        <p>Total meal counts:</p>
        <ul style={{ listStyle: "none", padding: 0 }}>
          <li>üç≥ Breakfast: {getMealCount("breakfast")}</li>
          <li>üçõ Lunch: {getMealCount("lunch")}</li>
          <li>üç≤ Supper: {getMealCount("supper")}</li>
        </ul>
      </div>
    );
  }

  return null;
}

export default App;
