import React, { useState } from "react";
import { auth, db } from "./firebase";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { doc, setDoc } from "firebase/firestore";

function SignupPage({ onSignupSuccess }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      // ðŸ”¹ Create user in Firebase Auth
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        email,
        password
      );

      const uid = userCredential.user.uid;

      // ðŸ”¹ Save user role as "student"
      await setDoc(doc(db, "users", uid), {
        email,
        role: "student",
      });

      // ðŸ”¹ Pre-fill next 7 days with meals marked as true
      const today = new Date();
      const userMealsRef = doc(db, "userMeals", uid);

      let mealData = {};
      for (let i = 0; i < 7; i++) {
        const date = new Date(today);
        date.setDate(today.getDate() + i);
        const dateKey = date.toISOString().split("T")[0]; // YYYY-MM-DD

        mealData[dateKey] = {
          breakfast: true,
          lunch: true,
          supper: true,
        };
      }

      await setDoc(userMealsRef, mealData, { merge: true });

      alert("Signup successful!");
      onSignupSuccess();
    } catch (err) {
      alert(err.message);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>Student Signup</h2>
      <form onSubmit={handleSignup}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <br />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <br />
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
}

export default SignupPage;
