// PointSystemPage.js
import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { doc, getDoc, setDoc } from "firebase/firestore";
import "./PointSystemPage.css";

function PointSystemPage({ goBack }) {
  const defaultSystem = {
    X: 1,
    "0": 0,
    B: 0.25,
    L: 0.35,
    S: 0.45,
    "B/L": 0.65,
    "B/S": 0.75,
    "L/S": 0.85,
  };

  const [pointSystem, setPointSystem] = useState(defaultSystem);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPoints = async () => {
      const ref = doc(db, "settings", "points");
      const snap = await getDoc(ref);
      if (snap.exists()) {
        setPointSystem(snap.data());
      } else {
        await setDoc(ref, defaultSystem);
      }
      setLoading(false);
    };
    fetchPoints();
  }, []);

  const saveSystem = async () => {
    try {
      await setDoc(doc(db, "settings", "points"), pointSystem);
      alert("✅ Point system updated!");
    } catch (err) {
      alert("⚠️ " + err.message);
    }
  };

  if (loading) return <h2 className="points-loading">Loading point system...</h2>;

  return (
    <div className="points-container">
      <h2 className="points-title">⚙️ Manage Point System</h2>
      <p className="points-sub">Edit values and click Save. (Keys are combinations of meals UNMARKED)</p>

      <table className="points-table">
        <thead>
          <tr>
            <th>Rule</th>
            <th>Value</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(pointSystem).map((key) => (
            <tr key={key}>
              <td className="rule-key">{key}</td>
              <td>
                <input
                  className="rule-input"
                  type="number"
                  step="0.01"
                  value={pointSystem[key]}
                  onChange={(e) =>
                    setPointSystem({
                      ...pointSystem,
                      [key]: parseFloat(e.target.value),
                    })
                  }
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="points-actions">
        <button className="btn save-btn" onClick={saveSystem}>Save</button>
        <button className="btn back-btn" onClick={goBack}>⬅ Back to Dashboard</button>
      </div>
    </div>
  );
}

export default PointSystemPage;
