import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import {
  doc,
  getDoc,
  setDoc,
  updateDoc,
  arrayUnion,
  arrayRemove,
} from "firebase/firestore";

function StudentPage({ currentUser }) {
  const [mealMarked, setMealMarked] = useState({});
  const [next7Days, setNext7Days] = useState([]);

  // 🔹 Generate next 7 days
  useEffect(() => {
    const days = [];
    const today = new Date();
    for (let i = 0; i < 7; i++) {
      const d = new Date(today);
      d.setDate(today.getDate() + i);
      const dateStr = d.toISOString().split("T")[0]; // YYYY-MM-DD
      days.push(dateStr);
    }
    setNext7Days(days);
  }, []);

  // 🔹 Ensure default meals are marked
  useEffect(() => {
    const setupDefaultMarks = async () => {
      const meals = ["breakfast", "lunch", "supper"];
      const marks = {};

      for (const date of next7Days) {
        marks[date] = {};
        for (const meal of meals) {
          const mealRef = doc(db, "meals", meal, "days", date);
          const mealSnap = await getDoc(mealRef);

          if (mealSnap.exists()) {
            const users = mealSnap.data().users || [];
            if (!users.includes(currentUser.uid)) {
              // auto-add new student
              await updateDoc(mealRef, {
                users: arrayUnion(currentUser.uid),
              });
            }
            marks[date][meal] = users.includes(currentUser.uid);
          } else {
            // create day with this student marked by default
            await setDoc(mealRef, { users: [currentUser.uid] });
            marks[date][meal] = true;
          }
        }
      }
      setMealMarked(marks);
    };

    if (next7Days.length > 0) setupDefaultMarks();
  }, [next7Days, currentUser]);

  // 🔹 Check if changes allowed (before 11:59 PM previous day)
  const canMark = (dateStr) => {
    const now = new Date();
    const targetDate = new Date(dateStr);
    const deadline = new Date(targetDate);
    deadline.setDate(targetDate.getDate() - 1);
    deadline.setHours(23, 59, 59, 999);
    return now <= deadline;
  };

  // 🔹 Toggle meal
  const toggleMeal = async (meal, dateStr) => {
    if (!canMark(dateStr)) {
      alert("❌ You cannot change this meal anymore (deadline passed).");
      return;
    }

    try {
      const mealRef = doc(db, "meals", meal, "days", dateStr);
      const mealSnap = await getDoc(mealRef);

      if (mealSnap.exists()) {
        const data = mealSnap.data();
        if (data.users && data.users.includes(currentUser.uid)) {
          // unmark
          await updateDoc(mealRef, {
            users: arrayRemove(currentUser.uid),
          });
          setMealMarked((prev) => ({
            ...prev,
            [dateStr]: { ...prev[dateStr], [meal]: false },
          }));
        } else {
          // mark
          await updateDoc(mealRef, {
            users: arrayUnion(currentUser.uid),
          });
          setMealMarked((prev) => ({
            ...prev,
            [dateStr]: { ...prev[dateStr], [meal]: true },
          }));
        }
      }
    } catch (err) {
      console.error(err);
    }
  };

  // 🔹 Button style helper
  const getButtonStyle = (marked, allowed) => ({
    margin: "5px",
    padding: "10px 15px",
    border: "none",
    borderRadius: "8px",
    cursor: allowed ? "pointer" : "not-allowed",
    backgroundColor: marked ? "#90EE90" : "#FF7F7F", // light green or light red
    opacity: allowed ? 1 : 0.6,
    fontWeight: "bold",
  });

  return (
    <div style={{ textAlign: "center", marginTop: "30px" }}>
      <h2>Welcome, {currentUser.email}</h2>
      <h3>📅 Meals (Next 7 Days)</h3>

      {next7Days.map((dateStr) => (
        <div key={dateStr} style={{ marginBottom: "20px" }}>
          <h4>{dateStr}</h4>

          <button
            style={getButtonStyle(mealMarked[dateStr]?.breakfast, canMark(dateStr))}
            disabled={!canMark(dateStr)}
            onClick={() => toggleMeal("breakfast", dateStr)}
          >
            {mealMarked[dateStr]?.breakfast
              ? "✅ Breakfast (Unmark"
              : "❌ Breakfast "}
          </button>

          <button
            style={getButtonStyle(mealMarked[dateStr]?.lunch, canMark(dateStr))}
            disabled={!canMark(dateStr)}
            onClick={() => toggleMeal("lunch", dateStr)}
          >
            {mealMarked[dateStr]?.lunch
              ? " Lunch "
              : " Lunch "}
          </button>

          <button
            style={getButtonStyle(mealMarked[dateStr]?.supper, canMark(dateStr))}
            disabled={!canMark(dateStr)}
            onClick={() => toggleMeal("supper", dateStr)}
          >
            {mealMarked[dateStr]?.supper
              ? " Supper"
              : " Supper"}
          </button>
        </div>
      ))}
    </div>
  );
}

export default StudentPage;
