// firebase.js
import { initializeApp } from "firebase/app";
import { 
  getAuth, 
  GoogleAuthProvider, 
  signInWithPopup, 
  signOut,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  sendPasswordResetEmail
} from "firebase/auth";
import { getFirestore, disableNetwork } from "firebase/firestore";

// üîπ Your Firebase config (must match Firebase Console ‚Üí Project Settings)
const firebaseConfig = {
  apiKey: "AIzaSyCUsi5trZiXqkxIen_9wiiNaub7-9XKfBw",
  authDomain: "mess-app-dab87.firebaseapp.com",
  projectId: "mess-app-dab87",
  storageBucket: "mess-app-dab87.appspot.com",  // ‚úÖ corrected
  messagingSenderId: "866142996355",
  appId: "1:866142996355:web:324d96a7cb8dac404c58b6"
};

// üîπ Initialize Firebase
const app = initializeApp(firebaseConfig);

// üîπ Initialize services
const auth = getAuth(app);
const db = getFirestore(app);

// üîπ Force Firestore online only (disable cache mode)
disableNetwork(db).catch(() => {
  console.warn("‚ö†Ô∏è Firestore network disable call failed, but continuing...");
});

const provider = new GoogleAuthProvider();

// üîπ Export everything
export {
  provider,
  signInWithPopup,
  signOut,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  sendPasswordResetEmail
};
