import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { doc, onSnapshot } from "firebase/firestore";

function AdminPage() {
  const [mealCounts, setMealCounts] = useState({});
  const [next7Days, setNext7Days] = useState([]);

  // üîπ Generate next 7 days
  useEffect(() => {
    const days = [];
    const today = new Date();
    for (let i = 0; i < 7; i++) {
      const d = new Date(today);
      d.setDate(today.getDate() + i);
      const dateStr = d.toISOString().split("T")[0];
      days.push(dateStr);
    }
    setNext7Days(days);
  }, []);

  // üîπ Listen for updates
  useEffect(() => {
    const meals = ["breakfast", "lunch", "supper"];
    const unsubscribers = [];

    for (const date of next7Days) {
      for (const meal of meals) {
        const unsub = onSnapshot(doc(db, "meals", meal, "days", date), (snap) => {
          setMealCounts((prev) => ({
            ...prev,
            [date]: {
              ...prev[date],
              [meal]: snap.exists() ? (snap.data().users?.length || 0) : 0,
            },
          }));
        });
        unsubscribers.push(unsub);
      }
    }
    return () => unsubscribers.forEach((u) => u());
  }, [next7Days]);

  return (
    <div style={{ textAlign: "center", marginTop: "30px" }}>
      <h2>üìä Admin Dashboard</h2>
      <h3>Meal Counts for Next 7 Days</h3>

      {next7Days.map((dateStr) => (
        <div key={dateStr} style={{ marginBottom: "20px" }}>
          <h4>{dateStr}</h4>
          <p>üç≥ Breakfast: {mealCounts[dateStr]?.breakfast || 0}</p>
          <p>üçõ Lunch: {mealCounts[dateStr]?.lunch || 0}</p>
          <p>üç≤ Supper: {mealCounts[dateStr]?.supper || 0}</p>
        </div>
      ))}
    </div>
  );
}

export default AdminPage;
