import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import {
  doc,
  getDoc,
  setDoc,
  updateDoc,
  arrayUnion,
  arrayRemove,
} from "firebase/firestore";

function StudentPage({ currentUser }) {
  const [mealMarked, setMealMarked] = useState({
    breakfast: false,
    lunch: false,
    supper: false,
  });

  // ðŸ”¹ Load current student's marks
  useEffect(() => {
    const fetchMarks = async () => {
      const meals = ["breakfast", "lunch", "supper"];
      for (const meal of meals) {
        const mealRef = doc(db, "meals", meal);
        const mealSnap = await getDoc(mealRef);
        if (mealSnap.exists()) {
          const users = mealSnap.data().users || [];
          if (users.includes(currentUser.uid)) {
            setMealMarked((prev) => ({ ...prev, [meal]: true }));
          }
        }
      }
    };
    fetchMarks();
  }, [currentUser]);

  // ðŸ”¹ Mark/Unmark a meal
  const toggleMeal = async (meal) => {
    try {
      const mealRef = doc(db, "meals", meal);
      const mealSnap = await getDoc(mealRef);

      if (mealSnap.exists()) {
        const data = mealSnap.data();
        if (data.users && data.users.includes(currentUser.uid)) {
          // unmark
          await updateDoc(mealRef, {
            users: arrayRemove(currentUser.uid),
          });
          setMealMarked((prev) => ({ ...prev, [meal]: false }));
        } else {
          // mark
          await updateDoc(mealRef, {
            users: arrayUnion(currentUser.uid),
          });
          setMealMarked((prev) => ({ ...prev, [meal]: true }));
        }
      } else {
        // create new doc
        await setDoc(mealRef, {
          users: [currentUser.uid],
        });
        setMealMarked((prev) => ({ ...prev, [meal]: true }));
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>Welcome, {currentUser.email}</h2>
      <h3>Mark your meals</h3>
      <button onClick={() => toggleMeal("breakfast")}>
        {mealMarked.breakfast ? "Unmark Breakfast" : "Mark Breakfast"}
      </button>
      <button onClick={() => toggleMeal("lunch")}>
        {mealMarked.lunch ? "Unmark Lunch" : "Mark Lunch"}
      </button>
      <button onClick={() => toggleMeal("supper")}>
        {mealMarked.supper ? "Unmark Supper" : "Mark Supper"}
      </button>
    </div>
  );
}

export default StudentPage;
